import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaDownload, FaTrash } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  const handleDelete = async (noteId) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;
    try {
      await API.delete(`/api/resources/notes/${noteId}`);
      fetchNotes();
    } catch (err) {
      console.error('Error deleting note:', err);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-4xl font-bold text-center text-indigo-700 mb-10">
        Manage Notes
      </h2>
      {notes.length === 0 ? (
        <p className="text-center text-gray-500">No notes uploaded yet.</p>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {notes.map((note) => (
            <div
              key={note._id}
              className="bg-white border border-gray-200 rounded-xl shadow-md p-5 flex flex-col justify-between"
            >
              <div>
                <h3 className="text-xl font-semibold text-indigo-800 text-center mb-3">
                  {note.title || 'Untitled Note'}
                </h3>
                <iframe
                  src={note.fileUrl}
                  title={note.title}
                  className="w-full h-64 border border-gray-300 rounded-md mb-4"
                />
              </div>
              <div className="flex justify-between mt-2">
                <a
                  href={note.fileUrl}
                  download
                  className="inline-flex items-center gap-2 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded text-sm"
                >
                  <FaDownload /> Download
                </a>
                <button
                  onClick={() => handleDelete(note._id)}
                  className="inline-flex items-center gap-2 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded text-sm"
                >
                  <FaTrash /> Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ManageNotes;
