import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaFilePdf, FaTrash, FaPlus, FaUpload } from 'react-icons/fa';

const Papers = () => {
  const [papers, setPapers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newPaper, setNewPaper] = useState({
    university: '',
    course: '',
    year: '',
    subject: '',
    pdf: null
  });

  useEffect(() => {
    axios.get('http://localhost:5000/api/papers')
      .then((res) => setPapers(res.data))
      .catch((err) => console.error('Failed to load papers:', err));
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/papers/${id}`);
      setPapers(papers.filter((paper) => paper._id !== id));
    } catch (error) {
      console.error('Delete failed:', error);
      alert('Failed to delete paper.');
    }
  };

  const handleAdd = async () => {
    const { university, course, year, subject, pdf } = newPaper;

    if (!university || !course || !year || !subject || !pdf) {
      return alert('Please fill in all fields.');
    }

    const formData = new FormData();
    formData.append('university', university);
    formData.append('course', course);
    formData.append('year', year);
    formData.append('subject', subject);
    formData.append('pdf', pdf);

    try {
      const res = await axios.post('http://localhost:5000/api/papers', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setPapers([...papers, res.data.paper]);
      setNewPaper({ university: '', course: '', year: '', subject: '', pdf: null });
      setShowForm(false);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('Upload failed');
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewPaper({ ...newPaper, pdf: file });
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-blue-700">ðŸ“„ Manage Question Papers</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-6 flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        <FaPlus /> {showForm ? 'Cancel' : 'Add Paper'}
      </button>

      {showForm && (
        <div className="mb-8 bg-gray-100 p-6 rounded-xl shadow space-y-4">
          {['university', 'course', 'year', 'subject'].map((field) => (
            <input
              key={field}
              type={field === 'year' ? 'number' : 'text'}
              name={field}
              placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
              value={newPaper[field]}
              onChange={(e) => setNewPaper({ ...newPaper, [field]: e.target.value })}
              className="w-full border px-3 py-2 rounded focus:outline-none focus:ring focus:ring-blue-300"
            />
          ))}

          <div>
            <label className="block mb-1 font-medium text-gray-700">Upload PDF</label>
            <label className="bg-blue-600 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-700 inline-flex items-center gap-2">
              <FaUpload />
              Choose File
              <input
                type="file"
                accept="application/pdf"
                onChange={handleFileChange}
                className="hidden"
              />
            </label>
            {newPaper.pdf && (
              <span className="ml-3 text-sm text-gray-700">{newPaper.pdf.name}</span>
            )}
          </div>

          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-5 py-2 rounded hover:bg-green-700"
          >
            Submit Paper
          </button>
        </div>
      )}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {papers.map((paper) => (
          <div
            key={paper._id}
            className="bg-white rounded-xl shadow p-4 border border-gray-200 dark:bg-gray-800 dark:border-gray-700"
          >
            <h3 className="text-xl font-semibold text-blue-800 mb-1">
              {paper.subject} ({paper.year})
            </h3>
            <p className="text-sm text-gray-600 dark:text-gray-400 mb-1">
              {paper.course} - {paper.university}
            </p>
            <a
              href={`http://localhost:5000/uploads/${paper.pdf}`}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center gap-2 text-blue-600 text-sm hover:underline"
            >
              <FaFilePdf /> View PDF
            </a>

            <button
              onClick={() => handleDelete(paper._id)}
              className="mt-3 inline-flex items-center gap-1 bg-red-500 text-white text-sm px-3 py-1 rounded hover:bg-red-600"
            >
              <FaTrash /> Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Papers;
