import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaEdit, FaTrash, FaSave } from 'react-icons/fa';

const BASE_URL = 'http://localhost:5000';

const AdminDepartments = () => {
  const [universities, setUniversities] = useState([]);
  const [selectedUniversityId, setSelectedUniversityId] = useState('');
  const [departments, setDepartments] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);
  const [newName, setNewName] = useState('');

  useEffect(() => {
    const fetchUniversities = async () => {
      try {
        const res = await axios.get(`${BASE_URL}/api/universities`);
        setUniversities(res.data);
      } catch (error) {
        console.error('Error fetching universities:', error);
      }
    };

    fetchUniversities();
  }, []);

  useEffect(() => {
    if (!selectedUniversityId) {
      setDepartments([]);
      return;
    }

    const selected = universities.find((u) => u._id === selectedUniversityId);
    if (selected) {
      setDepartments(selected.departments || []);
    }
  }, [selectedUniversityId, universities]);

  const handleEdit = (index) => {
    setEditingIndex(index);
    setNewName(departments[index].name);
  };

  const handleUpdate = async (deptId) => {
    try {
      await axios.put(
        `${BASE_URL}/api/universities/${selectedUniversityId}/departments/${deptId}`,
        { name: newName }
      );
      const updated = [...departments];
      updated[editingIndex].name = newName;
      setDepartments(updated);
      setEditingIndex(null);
      alert('✅ Department updated');
    } catch (err) {
      console.error('Update failed:', err);
      alert('❌ Could not update department');
    }
  };

  const handleDelete = async (deptId) => {
    try {
      await axios.delete(
        `${BASE_URL}/api/universities/${selectedUniversityId}/departments/${deptId}`
      );
      const filtered = departments.filter((d) => d._id !== deptId);
      setDepartments(filtered);
      alert('✅ Department deleted');
    } catch (err) {
      console.error('Delete failed:', err);
      alert('❌ Could not delete department');
    }
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">
        Manage Departments
      </h2>

      {/* University Selector */}
      <div className="mb-6 flex flex-col md:flex-row items-center gap-4">
        <label className="font-medium text-lg text-gray-700">
          Select University:
        </label>
        <select
          value={selectedUniversityId}
          onChange={(e) => setSelectedUniversityId(e.target.value)}
          className="border border-gray-300 p-2 rounded w-full md:w-1/2 focus:outline-none focus:ring focus:border-blue-400"
        >
          <option value="">-- Choose University --</option>
          {universities.map((uni) => (
            <option key={uni._id} value={uni._id}>
              {uni.name}
            </option>
          ))}
        </select>
      </div>

      {/* Department Cards */}
      <div className="grid sm:grid-cols-1 md:grid-cols-2 gap-4">
        {departments.map((dept, index) => (
          <div
            key={dept._id}
            className="bg-white shadow-md rounded-lg p-4 flex justify-between items-center transition hover:shadow-lg"
          >
            {editingIndex === index ? (
              <input
                type="text"
                value={newName}
                onChange={(e) => setNewName(e.target.value)}
                className="border p-2 flex-grow rounded mr-2 focus:outline-none focus:ring focus:border-blue-400"
              />
            ) : (
              <p className="text-gray-800 text-lg flex-grow">{dept.name}</p>
            )}

            <div className="flex gap-2 items-center ml-4">
              {editingIndex === index ? (
                <button
                  onClick={() => handleUpdate(dept._id)}
                  className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded flex items-center"
                >
                  <FaSave className="mr-1" />
                  Save
                </button>
              ) : (
                <button
                  onClick={() => handleEdit(index)}
                  className="text-blue-600 hover:text-blue-800 text-xl"
                  title="Edit"
                >
                  <FaEdit />
                </button>
              )}
              <button
                onClick={() => handleDelete(dept._id)}
                className="text-red-600 hover:text-red-800 text-xl"
                title="Delete"
              >
                <FaTrash />
              </button>
            </div>
          </div>
        ))}
      </div>

      {selectedUniversityId && departments.length === 0 && (
        <p className="text-gray-500 mt-4">No departments found.</p>
      )}
    </div>
  );
};

export default AdminDepartments;
