import React from 'react';
import { Link } from 'react-router-dom';
import backgroundImg from '../assets/background.png'; // Make sure the image exists here
import {
  FaFileAlt,
  FaBookOpen,
  FaUniversity,
  FaClipboardList,
  FaBrain,
  FaChalkboardTeacher,
  FaRegFilePdf,
} from 'react-icons/fa';
const Home = () => {
  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-gray-200 transition-colors duration-300">
      
      {/* Hero Section with Background Image */}
      <section
        className="relative text-center py-24 px-4 text-white bg-cover bg-center"
        style={{
          backgroundImage: `url(${backgroundImg})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
        }}
      >
        {/* Dark overlay */}
        <div className="absolute inset-0 bg-black bg-opacity-60 z-0"></div>

        {/* Hero Content */}
        <div className="relative z-10 max-w-3xl mx-auto">
          <h1 className="text-5xl font-extrabold mb-4 tracking-tight">Welcome to ARMS Portal</h1>
          <p className="text-lg md:text-xl max-w-xl mx-auto">
            Academic Resource Management System for Students and Faculty
          </p>
          <Link to="/dashboard">
            <button className="mt-8 px-8 py-3 bg-white text-blue-700 font-semibold rounded-xl shadow hover:bg-blue-100 transition">
              Go to Dashboard
            </button>
          </Link>
        </div>
      </section>

      {/* About Section */}
      <section className="py-16 px-6 max-w-5xl mx-auto text-center">
        <h2 className="text-3xl font-bold mb-4">What is ARMS?</h2>
        <p className="text-lg text-gray-600 dark:text-gray-300">
          ARMS is a centralized platform to manage and access academic resources like notes,
          syllabi, and previous year question papers from multiple universities.
        </p>
      </section>

   import { FaFileAlt, FaBookOpen, FaUniversity, FaClipboardList, FaBrain, FaChalkboardTeacher, FaRegFilePdf } from 'react-icons/fa';

{/* Features Section */}
<section className="py-16 bg-white dark:bg-gray-800 px-6">
  <h3 className="text-3xl font-bold text-center mb-12 text-blue-700 dark:text-blue-300">
    üîë Key Features
  </h3>

  <div className="grid gap-8 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 max-w-7xl mx-auto">
    {[
      {
        title: 'Notes',
        desc: 'Upload and access semester-wise notes in PDF format.',
        icon: <FaRegFilePdf size={40} className="text-blue-600 dark:text-blue-300 mx-auto" />
      },
      {
        title: 'Syllabus',
        desc: 'Explore and update academic syllabi for all departments.',
        icon: <FaBookOpen size={40} className="text-green-600 dark:text-green-300 mx-auto" />
      },
      {
        title: 'Question Papers',
        desc: 'Access past university question papers for all years.',
        icon: <FaFileAlt size={40} className="text-purple-600 dark:text-purple-300 mx-auto" />
      },
      {
        title: 'Academic Resources',
        desc: 'Centralized access to all learning materials.',
        icon: <FaClipboardList size={40} className="text-indigo-600 dark:text-indigo-300 mx-auto" />
      },
      {
        title: 'Exam Preparations',
        desc: 'Tools and papers to prepare efficiently.',
        icon: <FaBrain size={40} className="text-pink-600 dark:text-pink-300 mx-auto" />
      },
      {
        title: 'University Details',
        desc: 'Manage university profiles and content.',
        icon: <FaUniversity size={40} className="text-yellow-600 dark:text-yellow-300 mx-auto" />
      },
      {
        title: 'Courses Details',
        desc: 'Course and semester-wise structure & data.',
        icon: <FaChalkboardTeacher size={40} className="text-red-600 dark:text-red-300 mx-auto" />
      }
    ].map((f, i) => (
      <div
        key={i}
        className="bg-blue-50 dark:bg-gray-700 p-6 rounded-2xl shadow hover:shadow-xl transition transform hover:-translate-y-1 text-center"
      >
        <div className="mb-4">{f.icon}</div>
        <h4 className="text-xl font-semibold mb-2 text-blue-800 dark:text-white">{f.title}</h4>
        <p className="text-sm text-gray-700 dark:text-gray-300">{f.desc}</p>
      </div>
    ))}
  </div>
</section>



      {/* Testimonials */}
      <section className="py-20 bg-gradient-to-br from-sky-100 to-indigo-100 dark:from-gray-800 dark:to-gray-900">
        <div className="max-w-6xl mx-auto text-center px-6">
          <h3 className="text-3xl font-bold mb-10">Trusted By</h3>
          <div className="grid gap-8 md:grid-cols-3 text-gray-700 dark:text-gray-300">
            {[
              {
                quote:
                  'Thanks to ARMS Portal, finding study material has become 10x easier!',
                name: 'Ritika Mehta',
                role: 'Student - SPU Mandi',
              },
              {
                quote:
                  'As a teacher, uploading and organizing resources is now a breeze.',
                name: 'Dr. Neha Sharma',
                role: 'Faculty - SPU Mandi',
              },
              {
                quote:
                  'Multi-university management from one dashboard is a game changer.',
                name: 'System Admin',
                role: 'Academic Coordinator',
              },
            ].map((t, i) => (
              <div
                key={i}
                className="bg-white dark:bg-gray-700 p-6 rounded-xl shadow transition hover:shadow-md"
              >
                <p className="italic mb-4">‚Äú{t.quote}‚Äù</p>
                <div className="font-semibold">{t.name}</div>
                <div className="text-sm text-gray-500 dark:text-gray-400">{t.role}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

     
      
    </div>
  );
};

export default Home;
