import React, { useEffect, useState } from 'react';
import { FaUserShield, FaShoppingCart } from 'react-icons/fa';
import axios from 'axios';

const TrustedPage = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [purchasers, setPurchasers] = useState([]);

  useEffect(() => {
    // Replace with your actual API endpoints
    axios.get(`${process.env.REACT_APP_API_BASE}/trusted`)
      .then(res => setTrustedPersons(res.data))
      .catch(err => console.error('Trusted fetch error:', err));

    axios.get(`${process.env.REACT_APP_API_BASE}/purchasers`)
      .then(res => setPurchasers(res.data))
      .catch(err => console.error('Purchasers fetch error:', err));
  }, []);

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-3xl font-bold text-center text-blue-800 mb-6">
        <FaUserShield className="inline mr-2" /> Our Trusted Team
      </h2>
      <div className="grid md:grid-cols-3 sm:grid-cols-2 grid-cols-1 gap-6 mb-12">
        {trustedPersons.map((person) => (
          <div key={person._id} className="bg-white shadow-lg rounded-2xl p-4 text-center">
            <img
              src={`${process.env.REACT_APP_API_BASE}${person.image}`}
              alt={person.name}
              className="w-32 h-32 rounded-full mx-auto object-cover mb-4"
            />
            <h3 className="text-lg font-semibold text-gray-800">{person.name}</h3>
            <p className="text-sm text-gray-600">{person.position}</p>
            <p className="text-sm text-gray-500">{person.description}</p>
          </div>
        ))}
      </div>

      <h2 className="text-3xl font-bold text-center text-green-800 mb-6">
        <FaShoppingCart className="inline mr-2" /> Our Purchasers
      </h2>
      <div className="grid md:grid-cols-3 sm:grid-cols-2 grid-cols-1 gap-6">
        {purchasers.map((buyer) => (
          <div key={buyer._id} className="bg-white shadow-md rounded-xl p-4 flex items-center space-x-4">
            <img
              src={`${process.env.REACT_APP_API_BASE}${buyer.image}`}
              alt={buyer.name}
              className="w-20 h-20 object-cover rounded-full"
            />
            <div>
              <h4 className="text-lg font-semibold text-gray-900">{buyer.name}</h4>
              <p className="text-sm text-gray-600">Email: {buyer.email}</p>
              <p className="text-sm text-gray-600">Book: {buyer.bookName}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TrustedPage;
