import React from 'react';
import { Link } from 'react-router-dom';

const departmentCourses = [
  {
    course: 'Undergraduate (UG)',
    departments: [
      { name: 'Computer Science', programs: ['B.Tech CSE', 'B.Sc CS'] },
      { name: 'Electrical Engineering', programs: ['B.Tech EE'] },
      { name: 'Mechanical Engineering', programs: ['B.Tech ME'] }
    ]
  },
  {
    course: 'Postgraduate (PG)',
    departments: [
      { name: 'Computer Applications', programs: ['MCA', 'M.Sc IT'] },
      { name: 'Power Systems', programs: ['M.Tech Power Systems'] },
      { name: 'Thermal Engineering', programs: ['M.Tech Thermal'] }
    ]
  },
  {
    course: 'PhD',
    departments: [
      { name: 'AI Research', programs: ['PhD in AI'] },
      { name: 'VLSI Design', programs: ['PhD in VLSI'] },
      { name: 'Mechanical Dynamics', programs: ['PhD in Mech'] }
    ]
  }
];

const Departments = () => {
  return (
    <div className="p-6 md:p-12 bg-gray-50 min-h-screen">
      <h2 className="text-3xl font-bold text-center text-indigo-700 mb-10">
        Departments by Course
      </h2>

      {departmentCourses.map((group, idx) => (
        <div key={idx} className="mb-10">
          <h3 className="text-2xl font-semibold text-green-700 border-b border-green-300 pb-2 mb-6">
            {group.course}
          </h3>

          {group.departments.map((dept, deptIdx) => (
            <div key={deptIdx} className="mb-6">
              <h4 className="text-xl font-semibold text-blue-600 mb-4">
                {dept.name}
              </h4>

              <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
                {dept.programs.map((program, pIdx) => (
                  <Link
                    key={pIdx}
                    to={`/department-details/${program.toLowerCase().replace(/\s+/g, '-')}`}
                    className="bg-white border border-blue-200 p-5 rounded-xl shadow-sm hover:shadow-md hover:border-blue-400 transition-all duration-300 ease-in-out text-center"
                  >
                    <div className="text-lg font-semibold text-gray-800">
                      {program}
                    </div>
                    <div className="text-sm text-blue-600 mt-1">
                      View Notes, Papers & Syllabus
                    </div>
                  </Link>
                ))}
              </div>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Departments;
