import React, { useState } from 'react';
import axios from 'axios';
import {
  FaUniversity,
  FaGlobe,
  FaPlus,
  FaBook,
  FaLayerGroup,
  FaListAlt,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const UniversitiesPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    shortName: '',
    domain: '',
    courses: [''],
    departments: [{ name: '', programs: [''] }],
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleCourseChange = (index, value) => {
    const newCourses = [...formData.courses];
    newCourses[index] = value;
    setFormData((prev) => ({ ...prev, courses: newCourses }));
  };

  const addCourse = () => {
    setFormData((prev) => ({
      ...prev,
      courses: [...prev.courses, ''],
    }));
  };

  const handleDepartmentChange = (index, key, value) => {
    const newDepartments = [...formData.departments];
    newDepartments[index][key] = value;
    setFormData((prev) => ({ ...prev, departments: newDepartments }));
  };

  const handleProgramChange = (deptIndex, progIndex, value) => {
    const newDepartments = [...formData.departments];
    newDepartments[deptIndex].programs[progIndex] = value;
    setFormData((prev) => ({ ...prev, departments: newDepartments }));
  };

  const addProgram = (deptIndex) => {
    const newDepartments = [...formData.departments];
    newDepartments[deptIndex].programs.push('');
    setFormData((prev) => ({ ...prev, departments: newDepartments }));
  };

  const addDepartment = () => {
    setFormData((prev) => ({
      ...prev,
      departments: [...prev.departments, { name: '', programs: [''] }],
    }));
  };

  const handleSubmit = async () => {
    try {
      await API.post('/api/universities', formData);
      alert('✅ University added successfully!');
      setFormData({
        name: '',
        shortName: '',
        domain: '',
        courses: [''],
        departments: [{ name: '', programs: [''] }],
      });
    } catch (error) {
      console.error('Failed to submit:', error);
      alert('❌ Failed to add university');
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-semibold mb-4 flex gap-2 items-center text-indigo-700">
        <FaUniversity /> Add University
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input
          name="name"
          placeholder="University Name"
          value={formData.name}
          onChange={handleInputChange}
          className="border rounded-lg px-4 py-2 w-full"
        />
        <input
          name="shortName"
          placeholder="Short Name"
          value={formData.shortName}
          onChange={handleInputChange}
          className="border rounded-lg px-4 py-2 w-full"
        />
        <input
          name="domain"
          placeholder="Domain"
          value={formData.domain}
          onChange={handleInputChange}
          className="border rounded-lg px-4 py-2 w-full"
        />
      </div>

      <div className="mt-6">
        <h3 className="text-lg font-medium flex items-center gap-2 text-gray-800">
          <FaBook /> Courses
        </h3>
        {formData.courses.map((course, idx) => (
          <input
            key={idx}
            value={course}
            onChange={(e) => handleCourseChange(idx, e.target.value)}
            placeholder={`Course ${idx + 1}`}
            className="mt-2 w-full border rounded-lg px-4 py-2"
          />
        ))}
        <button
          onClick={addCourse}
          className="mt-2 text-blue-600 flex items-center gap-2"
        >
          <FaPlus /> Add Course
        </button>
      </div>

      <div className="mt-6">
        <h3 className="text-lg font-medium flex items-center gap-2 text-gray-800">
          <FaLayerGroup /> Departments & Programs
        </h3>
        {formData.departments.map((dept, deptIdx) => (
          <div key={deptIdx} className="mb-4 border p-3 rounded-lg">
            <input
              value={dept.name}
              onChange={(e) =>
                handleDepartmentChange(deptIdx, 'name', e.target.value)
              }
              placeholder={`Department ${deptIdx + 1}`}
              className="mb-2 w-full border rounded-lg px-4 py-2"
            />
            {dept.programs.map((program, progIdx) => (
              <input
                key={progIdx}
                value={program}
                onChange={(e) =>
                  handleProgramChange(deptIdx, progIdx, e.target.value)
                }
                placeholder={`Program ${progIdx + 1}`}
                className="mb-2 w-full border rounded-lg px-4 py-2"
              />
            ))}
            <button
              onClick={() => addProgram(deptIdx)}
              className="text-green-600 flex items-center gap-2"
            >
              <FaPlus /> Add Program
            </button>
          </div>
        ))}
        <button
          onClick={addDepartment}
          className="mt-2 text-blue-600 flex items-center gap-2"
        >
          <FaPlus /> Add Department
        </button>
      </div>

      <button
        onClick={handleSubmit}
        className="mt-6 w-full bg-indigo-600 text-white py-2 rounded-lg hover:bg-indigo-700 transition"
      >
        Submit University
      </button>
    </div>
  );
};

export default UniversitiesPage;
