import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const Footer = () => {
  const [date, setDate] = useState(new Date());
  const [darkMode, setDarkMode] = useState(false);

  const toggleReadingMode = () => {
    document.documentElement.classList.toggle('dark');
    setDarkMode(!darkMode);
  };

  return (
    <footer className="bg-blue-50 dark:bg-gray-900 text-gray-700 dark:text-gray-300 border-t border-gray-200 dark:border-gray-700 py-10 px-6 mt-10">
      <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-10 items-start">
        {/* Brand Section */}
        <div>
          <h2 className="text-2xl font-bold text-blue-700 dark:text-white mb-2">ARMS Portal</h2>
          <p className="text-sm">Empowering students and faculty with centralized academic resources.</p>
          <p className="text-sm mt-4">
            &copy; {new Date().getFullYear()} <span className="text-blue-600 dark:text-blue-300">Gautav & Team</span>
          </p>
        </div>

        {/* Calendar Section */}
        <div className="mx-auto md:mx-0">
          <h3 className="text-lg font-semibold mb-3 text-blue-600 dark:text-blue-300">Calendar</h3>
          <Calendar
            onChange={setDate}
            value={date}
            className="rounded-xl p-2 bg-white dark:bg-gray-800 text-sm"
          />
        </div>

        {/* Reading Mode Section */}
        <div className="text-center md:text-right">
          <h3 className="text-lg font-semibold mb-3 text-blue-600 dark:text-blue-300">Reading Mode</h3>
          <button
            onClick={toggleReadingMode}
            className="px-4 py-2 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition"
          >
            {darkMode ? 'Disable Dark Mode' : 'Enable Dark Mode'}
          </button>
        </div>
      </div>

      {/* Bottom Notice */}
      <div className="text-center mt-8 text-xs text-gray-500 dark:text-gray-400">
        Â© {new Date().getFullYear()} ARMS Portal. All rights reserved.
      </div>
    </footer>
  );
};

export default Footer;
