import React, { useEffect, useState } from 'react';

const EPlatform = () => {
  const [resources, setResources] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Load data from eresources.json on mount
  useEffect(() => {
    fetch('/data/eresources.json')
      .then(res => res.json())
      .then(data => setResources(data))
      .catch(err => console.error('Error loading eResources:', err));
  }, []);

  const filteredResources = resources.filter(resource =>
    resource.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    resource.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
    resource.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white py-10 px-4">
      <h1 className="text-4xl font-bold text-center text-blue-600 mb-6">E-Learning Resources</h1>

      <div className="max-w-3xl mx-auto mb-10">
        <input
          type="text"
          placeholder="Search tutorials, libraries, books..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full px-6 py-3 border border-blue-300 rounded-2xl focus:outline-none focus:ring-2 focus:ring-blue-400 shadow"
        />
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredResources.map((resource, index) => (
          <div key={index} className="bg-white shadow-md rounded-2xl p-5 hover:shadow-xl transition duration-300 flex flex-col">
            <img src={resource.image} alt={resource.title} className="w-full h-40 object-contain mb-4 rounded" />
            <h2 className="text-xl font-semibold text-gray-800">{resource.title}</h2>
            <p className="text-gray-600 text-sm flex-grow">{resource.description}</p>
            <span className="mt-2 text-xs inline-block bg-blue-100 text-blue-700 px-3 py-1 rounded-full">
              {resource.category}
            </span>
            <a
              href={resource.link}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-block mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
            >
              Visit
            </a>
          </div>
        ))}
      </div>

      {filteredResources.length === 0 && (
        <p className="text-center text-gray-500 mt-10 text-lg">No results for "<strong>{searchTerm}</strong>"</p>
      )}
    </div>
  );
};

export default EPlatform;
