import React, { useState } from 'react';
import axios from 'axios';
import {
  FaUniversity,
  FaGlobe,
  FaPlus,
  FaBook,
  FaLayerGroup,
  FaListAlt,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE
});

const AddUniversities = () => {
  const [formData, setFormData] = useState({
    name: '',
    shortName: '',
    description: '',
    website: '',
    logo: '',
    location: '',
    departments: [
      {
        name: '',
        courses: [{ name: '', level: '', duration: '', semesters: '' }]
      }
    ]
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleDepartmentChange = (index, e) => {
    const { name, value } = e.target;
    const updated = [...formData.departments];
    updated[index][name] = value;
    setFormData(prev => ({ ...prev, departments: updated }));
  };

  const handleCourseChange = (deptIndex, courseIndex, e) => {
    const { name, value } = e.target;
    const updated = [...formData.departments];
    updated[deptIndex].courses[courseIndex][name] = value;
    setFormData(prev => ({ ...prev, departments: updated }));
  };

  const addDepartment = () => {
    setFormData(prev => ({
      ...prev,
      departments: [...prev.departments, { name: '', courses: [{ name: '', level: '', duration: '', semesters: '' }] }]
    }));
  };

  const addCourse = (deptIndex) => {
    const updated = [...formData.departments];
    updated[deptIndex].courses.push({ name: '', level: '', duration: '', semesters: '' });
    setFormData(prev => ({ ...prev, departments: updated }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post('/api/universities', formData);
      if (res.status === 200 || res.status === 201) {
        setMessage('✅ University added successfully!');
        setFormData({
          name: '',
          shortName: '',
          description: '',
          website: '',
          logo: '',
          location: '',
          departments: [
            {
              name: '',
              courses: [{ name: '', level: '', duration: '', semesters: '' }]
            }
          ]
        });
      } else {
        setMessage(`❌ Failed: ${res.data.error || 'Server error'}`);
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('❌ Failed to connect to server');
    }
  };

  return (
    <div className="max-w-5xl mx-auto bg-white p-8 rounded-xl shadow-xl">
      <h2 className="text-3xl font-bold mb-6 text-center text-indigo-700 flex items-center justify-center gap-2">
        <FaUniversity /> Add New University
      </h2>

      {message && <div className="mb-4 text-blue-600 text-center font-semibold">{message}</div>}

      <form onSubmit={handleSubmit} className="space-y-5">
        {/* Basic Info */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input name="name" placeholder="University Name *" value={formData.name} onChange={handleChange} required className="border px-4 py-2 rounded w-full" />
          <input name="shortName" placeholder="Short Name (e.g. HPU)" value={formData.shortName} onChange={handleChange} className="border px-4 py-2 rounded w-full" />
          <input name="location" placeholder="Location" value={formData.location} onChange={handleChange} className="border px-4 py-2 rounded w-full" />
          <input name="website" placeholder="Website URL" value={formData.website} onChange={handleChange} className="border px-4 py-2 rounded w-full" />
          <input name="logo" placeholder="Logo URL or Path" value={formData.logo} onChange={handleChange} className="border px-4 py-2 rounded w-full" />
        </div>
        <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} className="w-full border px-4 py-2 rounded" />

        {/* Departments Section */}
        <div className="mt-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
            <FaBook /> Departments & Courses
          </h3>

          {formData.departments.map((dept, deptIndex) => (
            <div key={deptIndex} className="bg-gray-50 p-5 rounded-lg mb-6 border shadow">
              <div className="flex items-center justify-between mb-3">
                <h4 className="font-semibold text-lg text-gray-700 flex items-center gap-2">
                  <FaLayerGroup /> Department {deptIndex + 1}
                </h4>
              </div>

              <input
                name="name"
                placeholder="Department Name"
                value={dept.name}
                onChange={(e) => handleDepartmentChange(deptIndex, e)}
                className="border px-3 py-2 mb-4 rounded w-full"
              />

              {/* Courses Loop */}
              {dept.courses.map((course, courseIndex) => (
                <div key={courseIndex} className="grid grid-cols-1 md:grid-cols-2 gap-3 mb-4">
                  <input
                    name="name"
                    placeholder="Course Name"
                    value={course.name}
                    onChange={(e) => handleCourseChange(deptIndex, courseIndex, e)}
                    className="border px-3 py-2 rounded"
                  />
                  <select
                    name="level"
                    value={course.level}
                    onChange={(e) => handleCourseChange(deptIndex, courseIndex, e)}
                    className="border px-3 py-2 rounded"
                  >
                    <option value="">Select Level</option>
                    <option value="UG">UG</option>
                    <option value="PG">PG</option>
                    <option value="PhD">PhD</option>
                  </select>
                  <input
                    name="duration"
                    placeholder="Duration (e.g. 3 Years)"
                    value={course.duration}
                    onChange={(e) => handleCourseChange(deptIndex, courseIndex, e)}
                    className="border px-3 py-2 rounded col-span-2"
                  />
                  <input
                    name="semesters"
                    type="number"
                    placeholder="Total Semesters"
                    value={course.semesters}
                    onChange={(e) => handleCourseChange(deptIndex, courseIndex, e)}
                    className="border px-3 py-2 rounded col-span-2"
                  />
                </div>
              ))}

              {/* Add Course Button */}
              <div className="text-right">
                <button
                  type="button"
                  onClick={() => addCourse(deptIndex)}
                  className="inline-flex items-center gap-2 text-sm bg-blue-100 hover:bg-blue-200 text-blue-700 px-3 py-1 rounded"
                >
                  <FaPlus /> Add Course
                </button>
              </div>
            </div>
          ))}

          {/* Add Department Button */}
          <div className="text-center mt-4">
            <button
              type="button"
              onClick={addDepartment}
              className="inline-flex items-center gap-2 bg-green-100 hover:bg-green-200 text-green-700 px-4 py-2 rounded text-sm"
            >
              <FaPlus /> Add Department
            </button>
          </div>
        </div>

        {/* Submit Button */}
        <div className="text-center mt-8">
          <button type="submit" className="bg-indigo-600 text-white px-6 py-2 rounded hover:bg-indigo-700">
            Submit University
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddUniversities;
