import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const universities = [
  {
    name: 'Sardar Patel University, Mandi',
    description: 'A state university providing quality education in various technical and non-technical domains.',
    website: 'https://spumandi.ac.in',
    route: '/departments'
  },
  {
    name: 'Himachal Pradesh University',
    description: 'Renowned university offering UG and PG courses with strong research programs.',
    website: 'https://hpuniv.ac.in',
    route: '/departments'
  },
  {
    name: 'IIT Mandi',
    description: 'An Institute of National Importance offering cutting-edge research and engineering programs.',
    website: 'https://iitmandi.ac.in',
    route: '/departments'
  },
  {
    name: 'NIT Hamirpur',
    description: 'Premier technical institution offering UG, PG and doctoral programs in engineering, architecture, and sciences.',
    website: 'https://nith.ac.in',
    route: '/departments'
  },
  {
    name: 'Central University of Himachal Pradesh',
    description: 'A central university providing a variety of UG, PG, and PhD programs.',
    website: 'https://cuhimachal.ac.in',
    route: '/departments'
  }
];

const UniversityPage = () => {
  const navigate = useNavigate();

  return (
    <div className="p-8 text-center dark:bg-gray-800 dark:text-white bg-gray-100 min-h-screen">
      <h2 className="text-3xl font-bold text-blue-800 mb-8">Universities Resources</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6">
        {universities.map((uni, index) => (
          <div key={index} className="bg-white dark:bg-gray-900 p-4 rounded shadow text-left flex flex-col justify-between h-full">
            <div>
              <h3 className="text-lg font-semibold text-blue-700 dark:text-blue-400">{uni.name}</h3>
              <p className="text-sm my-2">{uni.description}</p>
            </div>
            <div className="flex flex-col gap-2 mt-2">
              <a
                href={uni.website}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 hover:underline"
              >
                Visit Website
              </a>
              <button
                onClick={() => navigate(uni.route)}
                className="text-sm bg-blue-500 text-white px-2 py-1 rounded hover:bg-blue-600"
              >
                View Departments
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UniversityPage;
