import React, { useEffect, useState } from 'react';
import API from '../lib/axios'; // adjust path if needed
 // âœ… Ensure this points to your custom axios instance

const AdminBooks = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [form, setForm] = useState({
    name: '',
    seller: '',
    email: '',
    price: '',
    image: '', // used for preview
  });
  const [imageFile, setImageFile] = useState(null);
  const [editingId, setEditingId] = useState(null);
  const [search, setSearch] = useState('');
  const [currentPage, setCurrentPage] = useState(1);

  const itemsPerPage = 5;

  useEffect(() => {
    fetchBooks();
  }, []);

  useEffect(() => {
    const term = search.toLowerCase();
    const filtered = books.filter(
      (b) =>
        b.name.toLowerCase().includes(term) ||
        b.seller.toLowerCase().includes(term)
    );
    setFilteredBooks(filtered);
    setCurrentPage(1);
  }, [search, books]);

  const fetchBooks = async () => {
    try {
      const res = await API.get('/api/books');
      setBooks(res.data);
    } catch (err) {
      console.error('Error fetching books:', err);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
    if (file) {
      setForm((prev) => ({
        ...prev,
        image: URL.createObjectURL(file),
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('name', form.name);
      formData.append('seller', form.seller);
      formData.append('email', form.email);
      formData.append('price', form.price);
      if (imageFile) formData.append('image', imageFile);

      if (editingId) {
        await API.put(`/api/books/${editingId}`, formData);
      } else {
        await API.post('/api/books', formData);
      }

      // Reset
      setForm({ name: '', seller: '', email: '', price: '', image: '' });
      setImageFile(null);
      setEditingId(null);
      fetchBooks();
    } catch (err) {
      console.error('Error saving book:', err);
    }
  };

  const handleEdit = (book) => {
    setForm({
      name: book.name,
      seller: book.seller,
      email: book.email,
      price: book.price,
      image: book.image,
    });
    setImageFile(null); // reset the image file
    setEditingId(book._id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      try {
        await API.delete(`/api/books/${id}`);
        fetchBooks();
      } catch (err) {
        console.error('Error deleting book:', err);
      }
    }
  };

  const paginatedBooks = filteredBooks.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const totalPages = Math.ceil(filteredBooks.length / itemsPerPage);

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">ðŸ“š Manage Books</h2>

      {/* Search */}
      <input
        type="text"
        placeholder="Search by name or seller..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-4 p-2 w-full border rounded"
      />

      {/* Form */}
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded p-4 mb-6" encType="multipart/form-data">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {['name', 'seller', 'email', 'price'].map((field) => (
            <input
              key={field}
              type={field === 'price' ? 'number' : 'text'}
              name={field}
              placeholder={`Enter ${field}`}
              value={form[field]}
              onChange={handleChange}
              className="border p-2 rounded"
              required
            />
          ))}
          <input
            type="file"
            name="image"
            accept="image/*"
            onChange={handleImageChange}
            className="border p-2 rounded"
            required={!editingId}
          />
        </div>

        {/* Preview Image */}
        {form.image && (
          <div className="mt-4">
            <p className="text-sm text-gray-500 mb-1">Image Preview:</p>
            <img
              src={form.image}
              alt="Preview"
              className="w-24 h-32 object-cover border rounded"
            />
          </div>
        )}

        <button
          type="submit"
          className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          {editingId ? 'Update Book' : 'Add Book'}
        </button>
      </form>

      {/* Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full table-auto bg-white shadow-md rounded">
          <thead className="bg-gray-200">
            <tr>
              <th className="p-2">Image</th>
              <th className="p-2">Name</th>
              <th className="p-2">Seller</th>
              <th className="p-2">Email</th>
              <th className="p-2">Price</th>
              <th className="p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {paginatedBooks.map((book) => (
              <tr key={book._id} className="text-center border-t">
                <td className="p-2">
                  <img src={book.image} alt={book.name} className="w-16 h-20 object-cover mx-auto" />
                </td>
                <td className="p-2">{book.name}</td>
                <td className="p-2">{book.seller}</td>
                <td className="p-2">{book.email}</td>
                <td className="p-2">â‚¹{book.price}</td>
                <td className="p-2 space-x-2">
                  <button
                    onClick={() => handleEdit(book)}
                    className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(book._id)}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {paginatedBooks.length === 0 && (
              <tr>
                <td colSpan="6" className="p-4 text-center text-gray-500">
                  No books found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      <div className="flex justify-center items-center gap-2 mt-4">
        <button
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
          disabled={currentPage === 1}
          className="px-3 py-1 bg-gray-300 rounded disabled:opacity-50"
        >
          Prev
        </button>
        <span className="text-sm text-gray-700">
          Page {currentPage} of {totalPages}
        </span>
        <button
          onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
          disabled={currentPage === totalPages}
          className="px-3 py-1 bg-gray-300 rounded disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default AdminBooks;
