import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const VerifyLoginRedirect = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');

    if (!token || !role) {
      navigate('/login');
    } else {
      if (role === 'admin') navigate('/admin-page/adminDashboard');
      else if (role === 'authority') navigate('/authority-page/AuthorityDashboard');
      else navigate('/user-page/UserDashboard');
    }
  }, [navigate]);

  return (
    <div className="h-screen flex items-center justify-center">
      <div className="text-center text-lg font-medium text-blue-700">
        Verifying login and redirecting...
      </div>
    </div>
  );
};

export default VerifyLoginRedirect;
