// src/admin-pages/AdminTrusted.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaUserShield, FaPlusCircle } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE || 'http://localhost:5000/api',
});

const AdminTrusted = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    position: '',
    description: '',
    image: null,
  });
  const [preview, setPreview] = useState(null);

  const fetchTrustedPersons = async () => {
    try {
      const res = await API.get('/trusted');
      setTrustedPersons(res.data);
    } catch (err) {
      console.error('Error fetching trusted persons:', err);
    }
  };

  useEffect(() => {
    fetchTrustedPersons();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      const file = files?.[0];
      setFormData((prev) => ({ ...prev, image: file || null }));
      setPreview(file ? URL.createObjectURL(file) : null);
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.image) return alert('Image required');

    const data = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      data.append(key, value);
    });

    try {
      await API.post('/trusted', data);
      fetchTrustedPersons();
      alert('Trusted person added');
      setFormData({ name: '', position: '', description: '', image: null });
      setPreview(null);
    } catch (err) {
      console.error('Upload failed:', err);
      alert('Upload failed');
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/trusted/${id}`);
      fetchTrustedPersons();
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  const buildImageUrl = (img) =>
    `${process.env.REACT_APP_API_BASE}/${img.replace(/\\/g, '/')}`;

  return (
    <div className="p-6 space-y-8">
      <h1 className="text-2xl font-bold flex items-center gap-2 text-blue-700">
        <FaUserShield /> Trusted Persons Manager
      </h1>

      <form
        onSubmit={handleSubmit}
        encType="multipart/form-data"
        className="bg-white p-4 shadow rounded grid gap-4 md:grid-cols-2 lg:grid-cols-3"
      >
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Name"
          required
          className="border p-2 rounded"
        />
        <input
          type="text"
          name="position"
          value={formData.position}
          onChange={handleChange}
          placeholder="Position"
          required
          className="border p-2 rounded"
        />
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleChange}
          placeholder="Description"
          className="border p-2 rounded"
        />
        <input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleChange}
          required
          className="border p-2 rounded"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded flex items-center justify-center"
        >
          <FaPlusCircle className="mr-2" /> Add
        </button>
        {preview && (
          <img
            src={preview}
            alt="Preview"
            className="w-24 h-24 object-cover border rounded-lg"
          />
        )}
      </form>

      <div>
        <h2 className="text-lg font-semibold text-green-700 mb-3">Trusted Persons</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {trustedPersons.map((person) => (
            <div key={person._id} className="bg-white p-4 rounded shadow relative">
              <img
                src={buildImageUrl(person.image)}
                alt={person.name}
                className="w-full h-40 object-cover rounded"
              />
              <h3 className="font-bold mt-2">{person.name}</h3>
              <p>{person.position}</p>
              <p className="text-sm text-gray-600">{person.description}</p>
              <button
                onClick={() => handleDelete(person._id)}
                className="absolute top-2 right-2 text-red-500"
              >
                <FaTrash />
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminTrusted;
