import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faBars,
  faTimes,
  faMoon,
  faSun
} from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

export default function Navbar() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);
  const toggleDarkMode = () => setDarkMode(!darkMode);

  useEffect(() => {
    const root = window.document.documentElement;
    darkMode ? root.classList.add('dark') : root.classList.remove('dark');
  }, [darkMode]);

  return (
    <>
      {/* Top Navbar */}
      <header className="sticky top-0 bg-white dark:bg-gray-900 text-black dark:text-white p-3 shadow flex justify-between items-center z-40">
        <button onClick={toggleSidebar} className="sm:hidden text-xl">
          <FontAwesomeIcon icon={faBars} />
        </button>
        <h1 className="text-lg font-semibold">ARMS</h1>
        <button onClick={toggleDarkMode} className="text-xl">
          <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
        </button>
      </header>

      {/* Sidebar */}
      <div className={`fixed  flex top-0 right-0 h-full w-full bg-blue-600 text-white p-4 z-50 transform ${sidebarOpen ? 'translate-x-0' : 'translate-x-full'} transition-transform duration-300 sm:translate-x-0 sm:left-0 sm:right-auto sm:relative`}>
        <div className="flex justify-end mb-4 sm:hidden">
          <button onClick={toggleSidebar}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>
        <nav className="space-y-3">
          <Link to="/" className="block hover:underline">Home</Link>
          <Link to="/departments" className="block hover:underline">Departments</Link>
          <Link to="/university" className="block hover:underline">University</Link>
          <Link to="/contact" className="block hover:underline">Contact Us</Link>
          <Link to="/members" className="block hover:underline">Special Members</Link>
          <Link to="/admin" className="block hover:underline">Admin Login</Link>
        </nav>
      </div>

     
    </>
  );
}
