import React, { useState } from 'react';

const AddSyllabus = () => {
  const [formData, setFormData] = useState({
    university: '',
    course: '',
    semester: '',
    subject: '',
    pdf: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({ ...formData, [name]: files ? files[0] : value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Syllabus submitted!');
  };

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Add Syllabus</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1 font-medium">University</label>
          <input type="text" name="university" value={formData.university} onChange={handleChange} className="w-full border rounded px-3 py-2" required />
        </div>
        <div>
          <label className="block mb-1 font-medium">Course</label>
          <input type="text" name="course" value={formData.course} onChange={handleChange} className="w-full border rounded px-3 py-2" required />
        </div>
        <div>
          <label className="block mb-1 font-medium">Semester</label>
          <input type="text" name="semester" value={formData.semester} onChange={handleChange} className="w-full border rounded px-3 py-2" required />
        </div>
        <div>
          <label className="block mb-1 font-medium">Subject</label>
          <input type="text" name="subject" value={formData.subject} onChange={handleChange} className="w-full border rounded px-3 py-2" required />
        </div>
        <div>
          <label className="block mb-1 font-medium">Upload PDF</label>
          <input type="file" name="pdf" accept="application/pdf" onChange={handleChange} className="w-full" required />
        </div>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
      </form>
    </div>
  );
};

export default AddSyllabus;
