import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [readingMode, setReadingMode] = useState(false);

  // Toggle class on <html> tag to enable dark mode globally
  useEffect(() => {
    if (readingMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [readingMode]);

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-gray-100 transition-colors duration-300">
      {/* Reading Mode Toggle */}
      <div className="flex justify-end p-4">
        <button
          onClick={() => setReadingMode(!readingMode)}
          className="px-4 py-2 bg-blue-600 text-white text-sm rounded-md hover:bg-blue-700 dark:bg-yellow-500 dark:hover:bg-yellow-600 transition"
        >
          {readingMode ? 'Exit Reading Mode' : 'Enter Reading Mode'}
        </button>
      </div>

      {/* Hero Section */}
      <section className="text-center py-16 px-4 bg-gradient-to-br from-blue-600 to-blue-800 text-white dark:from-gray-800 dark:to-gray-700">
        <h1 className="text-5xl font-extrabold mb-4 tracking-tight">Welcome to ARMS Portal</h1>
        <p className="text-xl mb-6">Academic Resource Management System for Students and Faculty</p>
        <Link to="/dashboard">
          <button className="mt-2 px-6 py-3 bg-white text-blue-700 font-semibold rounded-xl shadow hover:bg-blue-100 transition dark:bg-gray-100 dark:hover:bg-gray-300">
            Go to Dashboard
          </button>
        </Link>
      </section>

      {/* About Section */}
      <section className="py-12 px-6 max-w-4xl mx-auto text-center">
        <h2 className="text-3xl font-bold mb-4">What is ARMS?</h2>
        <p className="text-lg text-gray-600 dark:text-gray-300">
          ARMS (Academic Resource Management System) is a centralized platform to manage and access academic
          resources like notes, syllabi, and previous year question papers from multiple universities.
        </p>
      </section>

      {/* Features Section */}
      <section className="py-12 bg-white dark:bg-gray-800 px-4 transition-colors">
        <h3 className="text-2xl font-bold text-center mb-10 text-gray-800 dark:text-gray-100">Key Features</h3>
        <div className="grid gap-6 md:grid-cols-3 max-w-6xl mx-auto">
          <div className="bg-blue-50 dark:bg-gray-700 p-6 rounded-2xl shadow hover:shadow-lg transition">
            <h4 className="text-xl font-semibold mb-2">Notes</h4>
            <p className="text-gray-600 dark:text-gray-300">
              Upload and access department-wise, semester-wise notes in PDF format.
            </p>
          </div>
          <div className="bg-blue-50 dark:bg-gray-700 p-6 rounded-2xl shadow hover:shadow-lg transition">
            <h4 className="text-xl font-semibold mb-2">Syllabus</h4>
            <p className="text-gray-600 dark:text-gray-300">
              Explore and update academic syllabi for different courses and universities.
            </p>
          </div>
          <div className="bg-blue-50 dark:bg-gray-700 p-6 rounded-2xl shadow hover:shadow-lg transition">
            <h4 className="text-xl font-semibold mb-2">Question Papers</h4>
            <p className="text-gray-600 dark:text-gray-300">
              Manage year-wise university question papers for exam preparation.
            </p>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="text-center py-6 mt-10 text-sm text-gray-500 dark:text-gray-400">
        Â© {new Date().getFullYear()} ARMS Portal | Developed by Gautav
      </footer>
    </div>
  );
};

export default Home;
