// src/admin-pages/ManageNotes.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaEye, FaDownload, FaUniversity, FaBookOpen } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Failed to fetch notes:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;
    try {
      await API.delete(`/api/resources/${id}`);
      alert('âœ… Note deleted successfully.');
      fetchNotes();
    } catch (err) {
      console.error('âŒ Delete failed:', err);
      alert('âŒ Failed to delete note.');
    }
  };

  const handleView = (note) => {
    const fullUrl = `${process.env.REACT_APP_API_BASE}${note.fileUrl}`;
    window.open(fullUrl, '_blank', 'noopener,noreferrer');
  };

  const handleDownload = (note) => {
    const downloadUrl = `${process.env.REACT_APP_API_BASE}/api/resources/download/${note._id}`;
    window.open(downloadUrl, '_blank', 'noopener,noreferrer');
  };

  // Group by university and department
  const groupedNotes = notes.reduce((acc, note) => {
    const university = note.universityName || 'Unknown University';
    const department = note.department || 'General';
    if (!acc[university]) acc[university] = {};
    if (!acc[university][department]) acc[university][department] = [];
    acc[university][department].push(note);
    return acc;
  }, {});

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-4xl font-bold mb-10 text-center text-blue-800 flex items-center justify-center gap-3">
        <FaBookOpen className="text-green-600" /> Manage Uploaded Notes
      </h2>

      {Object.entries(groupedNotes).length === 0 ? (
        <p className="text-center text-gray-500">No notes available.</p>
      ) : (
        Object.entries(groupedNotes).map(([university, departments]) => (
          <div key={university} className="mb-12">
            <h3 className="text-3xl font-bold text-center text-purple-700 mb-6 flex items-center justify-center gap-2">
              <FaUniversity className="text-purple-600" />
              {university}
            </h3>

            {Object.entries(departments).map(([department, notesInDept]) => (
              <div key={department} className="mb-10">
                <h4 className="text-2xl font-semibold text-center text-blue-700 mb-4">
                  Department: {department}
                </h4>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {notesInDept.map((note) => (
                    <div key={note._id} className="bg-gray-50 rounded-lg p-5 shadow-md border flex flex-col justify-between">
                      <div className="text-center">
                        <h5 className="text-xl font-bold text-gray-800 mb-2">
                          ðŸ“˜ {note.title || 'Untitled Note'}
                        </h5>
                        <p className="text-base font-medium text-gray-600 mb-1">
                          {note.course} - Semester {note.semester}
                        </p>
                        <p className="text-sm text-gray-500 mb-2">
                          <strong>Type:</strong> {note.noteType || 'General'}
                        </p>
                      </div>

                      <div className="flex justify-center gap-3 mt-4">
                        <button
                          onClick={() => handleView(note)}
                          className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded flex items-center gap-1 text-sm"
                        >
                          <FaEye /> View
                        </button>

                        <button
                          onClick={() => handleDownload(note)}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded flex items-center gap-1 text-sm"
                        >
                          <FaDownload /> Download
                        </button>

                        <button
                          onClick={() => handleDelete(note._id)}
                          className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded flex items-center gap-1 text-sm"
                        >
                          <FaTrash /> Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default ManageNotes;
