import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaEye, FaDownload, FaUniversity, FaBookOpen, FaBuilding } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Failed to fetch notes:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;
    try {
      await API.delete(`/api/resources/${id}`);
      alert('✅ Note deleted successfully.');
      fetchNotes();
    } catch (err) {
      console.error('❌ Delete failed:', err);
      alert('❌ Failed to delete note.');
    }
  };

  const handleView = (note) => {
    const fullUrl = `${process.env.REACT_APP_API_BASE}${note.fileUrl}`;
    window.open(fullUrl, '_blank', 'noopener,noreferrer');
  };

  const handleDownload = async (note) => {
    try {
      const downloadUrl = `${process.env.REACT_APP_API_BASE}/api/resources/download/${note._id}`;
      window.open(downloadUrl, '_blank', 'noopener,noreferrer');
    } catch (err) {
      console.error('❌ Download failed:', err);
      alert('❌ Failed to download note.');
    }
  };

  // Group notes by university, then by department
  const groupedNotes = notes.reduce((acc, note) => {
    const uni = note.universityName;
    const dept = note.department;

    if (!acc[uni]) acc[uni] = {};
    if (!acc[uni][dept]) acc[uni][dept] = [];

    acc[uni][dept].push(note);
    return acc;
  }, {});

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-4xl font-bold mb-8 text-blue-800 flex items-center gap-3">
        <FaBookOpen className="text-green-600" /> Manage Uploaded Notes
      </h2>

      {Object.keys(groupedNotes).length === 0 ? (
        <p className="text-gray-500">No notes available.</p>
      ) : (
        Object.entries(groupedNotes).map(([universityName, departments]) => (
          <div key={universityName} className="mb-12">
            {/* University Name */}
            <h3 className="text-3xl font-semibold text-blue-700 flex items-center gap-2 mb-4">
              <FaUniversity className="text-purple-600" />
              {universityName}
            </h3>

            {Object.entries(departments).map(([deptName, notesInDept]) => (
              <div key={deptName} className="mb-8 bg-white p-4 rounded-xl shadow border">
                {/* Department Name */}
                <h4 className="text-xl font-medium text-gray-800 flex items-center gap-2 mb-4">
                  <FaBuilding className="text-gray-500" />
                  Department: {deptName}
                </h4>

                {/* Notes Cards */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {notesInDept.map((note) => (
                    <div key={note._id} className="bg-gray-50 rounded-lg p-6 shadow-sm border flex flex-col justify-between text-center">
                      {/* Course Centered */}
                      <div>
                        <h4 className="text-lg font-semibold text-gray-800 mb-2">
                          {note.course}
                        </h4>
                        <p className="text-sm text-gray-600 mb-4">
                          Semester: {note.semester}
                        </p>
                      </div>

                      {/* Buttons */}
                      <div className="mt-auto flex justify-center gap-2">
                        <button
                          onClick={() => handleView(note)}
                          className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm flex items-center gap-1"
                        >
                          <FaEye /> View
                        </button>

                        <button
                          onClick={() => handleDownload(note)}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm flex items-center gap-1"
                        >
                          <FaDownload /> Download
                        </button>

                        <button
                          onClick={() => handleDelete(note._id)}
                          className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm flex items-center gap-1"
                        >
                          <FaTrash /> Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default ManageNotes;
