import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaBook, FaDownload, FaTrashAlt } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Books = () => {
  const [books, setBooks] = useState([]);

  const fetchBooks = async () => {
    try {
      const res = await API.get('/resources/books');
      setBooks(res.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const deleteBook = async (id, filePath) => {
    try {
      await API.delete(`/resources/${id}`, {
        data: { filePath },
      });
      fetchBooks(); // Refresh list
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="max-w-5xl mx-auto px-4 py-8">
      <h1 className="text-3xl md:text-4xl font-bold text-blue-700 dark:text-blue-300 mb-6 text-center font-serif">
        ðŸ“š Academic Books Collection
      </h1>

      {books.length === 0 ? (
        <p className="text-center text-gray-600 dark:text-gray-300">No books available.</p>
      ) : (
        <div className="space-y-6">
          {books.map((book) => (
            <div
              key={book._id}
              className="flex flex-col md:flex-row items-start md:items-center justify-between bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-xl shadow-sm p-4 hover:shadow-md transition"
            >
              <div className="flex items-center gap-4 w-full md:w-auto">
                <FaBook className="text-blue-600 dark:text-blue-400 text-2xl" />
                <div>
                  <h2 className="text-lg font-semibold text-gray-800 dark:text-white">{book.originalname}</h2>
                  <p className="text-sm text-gray-500 dark:text-gray-400">
                    Uploaded on: {new Date(book.createdAt).toLocaleDateString()}
                  </p>
                </div>
              </div>

              <div className="flex gap-4 mt-4 md:mt-0 w-full md:w-auto justify-end">
                <a
                  href={`${process.env.REACT_APP_API_BASE}/${book.filePath}`}
                  download
                  className="flex items-center gap-2 px-3 py-2 bg-green-100 text-green-700 dark:bg-green-800 dark:text-green-300 rounded hover:bg-green-200 dark:hover:bg-green-700 transition"
                >
                  <FaDownload />
                  <span>Download</span>
                </a>
                <button
                  onClick={() => deleteBook(book._id, book.filePath)}
                  className="flex items-center gap-2 px-3 py-2 bg-red-100 text-red-700 dark:bg-red-800 dark:text-red-300 rounded hover:bg-red-200 dark:hover:bg-red-700 transition"
                >
                  <FaTrashAlt />
                  <span>Delete</span>
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Books;
