// src/admin-pages/AdminTrusted.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaEdit, FaUserShield, FaPlusCircle } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const AdminTrusted = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [purchasers, setPurchasers] = useState([]);
  const [formData, setFormData] = useState({
    type: 'trusted', // 'trusted' or 'purchaser'
    name: '',
    email: '',
    role: '',
    image: null,
  });

  // Fetch both lists
  const fetchData = async () => {
    const trustedRes = await API.get('/trusted');
    const purchaserRes = await API.get('/purchasers');
    setTrustedPersons(trustedRes.data);
    setPurchasers(purchaserRes.data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = (e) => {
    if (e.target.name === 'image') {
      setFormData({ ...formData, image: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    for (let key in formData) {
      data.append(key, formData[key]);
    }

    try {
      if (formData.type === 'trusted') {
        await API.post('/trusted', data);
      } else {
        await API.post('/purchasers', data);
      }
      fetchData();
      alert('Added successfully');
    } catch (err) {
      alert('Error: ' + err.message);
    }
  };

  const handleDelete = async (id, type) => {
    await API.delete(`/${type}/${id}`);
    fetchData();
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4 flex items-center gap-2">
        <FaUserShield /> Admin Trusted Manager
      </h1>

      {/* Form */}
      <form onSubmit={handleSubmit} className="mb-6 space-y-4">
        <div className="flex gap-4">
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="border p-2 rounded"
          >
            <option value="trusted">Trusted Person</option>
            <option value="purchaser">Purchaser</option>
          </select>
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={formData.name}
            onChange={handleChange}
            required
            className="border p-2 rounded w-full"
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            className="border p-2 rounded w-full"
          />
          <input
            type="text"
            name="role"
            placeholder="Role / Note"
            value={formData.role}
            onChange={handleChange}
            className="border p-2 rounded w-full"
          />
          <input
            type="file"
            name="image"
            accept="image/*"
            onChange={handleChange}
            required
            className="border p-2 rounded"
          />
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            <FaPlusCircle className="inline mr-1" /> Add
          </button>
        </div>
      </form>

      {/* Display Trusted Persons */}
      <h2 className="text-lg font-semibold my-4">Trusted Persons</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {trustedPersons.map((person) => (
          <div
            key={person._id}
            className="border rounded-lg p-4 shadow bg-white relative"
          >
            <img
              src={`${process.env.REACT_APP_API_BASE}/${person.image}`}
              alt={person.name}
              className="w-full h-48 object-cover rounded"
            />
            <div className="mt-2">
              <h3 className="font-bold">{person.name}</h3>
              <p>{person.email}</p>
              <p className="text-sm text-gray-600">{person.role}</p>
            </div>
            <button
              className="absolute top-2 right-2 text-red-500 hover:text-red-700"
              onClick={() => handleDelete(person._id, 'trusted')}
            >
              <FaTrash />
            </button>
          </div>
        ))}
      </div>

      {/* Display Purchasers */}
      <h2 className="text-lg font-semibold mt-10 mb-4">Purchasers</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {purchasers.map((buyer) => (
          <div
            key={buyer._id}
            className="border rounded-lg p-4 shadow bg-white relative"
          >
            <img
              src={`${process.env.REACT_APP_API_BASE}/${buyer.image}`}
              alt={buyer.name}
              className="w-full h-48 object-cover rounded"
            />
            <div className="mt-2">
              <h3 className="font-bold">{buyer.name}</h3>
              <p>{buyer.email}</p>
              <p className="text-sm text-gray-600">{buyer.role}</p>
            </div>
            <button
              className="absolute top-2 right-2 text-red-500 hover:text-red-700"
              onClick={() => handleDelete(buyer._id, 'purchasers')}
            >
              <FaTrash />
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminTrusted;
