import React, { useState } from 'react';

const Papers = () => {
  const [papers, setPapers] = useState([
    {
      university: 'Sardar Patel University, Mandi',
      course: 'B.Tech CSE',
      year: '2023',
      subject: 'Data Structures',
      pdf: 'sample-ds-2023.pdf'
    },
    {
      university: 'IIT Mandi',
      course: 'B.Tech EE',
      year: '2022',
      subject: 'Circuits',
      pdf: 'sample-circuits-2022.pdf'
    }
  ]);

  const [showForm, setShowForm] = useState(false);
  const [newPaper, setNewPaper] = useState({
    university: '',
    course: '',
    year: '',
    subject: '',
    pdf: ''
  });

  const handleDelete = (index) => {
    const updated = papers.filter((_, i) => i !== index);
    setPapers(updated);
  };

  const handleAdd = () => {
    if (newPaper.university && newPaper.course && newPaper.year && newPaper.subject && newPaper.pdf) {
      setPapers([...papers, newPaper]);
      setNewPaper({ university: '', course: '', year: '', subject: '', pdf: '' });
      setShowForm(false);
    } else {
      alert('Please fill in all fields.');
    }
  };

  return (
    <div className="p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Manage Question Papers</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        {showForm ? 'Cancel' : 'Add Paper'}
      </button>

      {showForm && (
        <div className="mb-6 bg-gray-100 p-4 rounded shadow space-y-3">
          <input
            type="text"
            placeholder="University"
            value={newPaper.university}
            onChange={(e) => setNewPaper({ ...newPaper, university: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            placeholder="Course"
            value={newPaper.course}
            onChange={(e) => setNewPaper({ ...newPaper, course: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="number"
            placeholder="Year"
            value={newPaper.year}
            onChange={(e) => setNewPaper({ ...newPaper, year: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            placeholder="Subject"
            value={newPaper.subject}
            onChange={(e) => setNewPaper({ ...newPaper, subject: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            placeholder="PDF Filename or URL"
            value={newPaper.pdf}
            onChange={(e) => setNewPaper({ ...newPaper, pdf: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Submit
          </button>
        </div>
      )}

      <div className="space-y-4">
        {papers.map((paper, index) => (
          <div key={index} className="border p-4 rounded shadow-sm">
            <h3 className="text-lg font-semibold text-blue-700">{paper.subject} ({paper.year})</h3>
            <p className="text-sm text-gray-600">{paper.course} - {paper.university}</p>
            <a
              href={paper.pdf}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-500 text-sm hover:underline"
            >
              View PDF
            </a>
            <div className="mt-2">
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Papers;
