import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddCourses = () => {
  const [formData, setFormData] = useState({
    name: '',
    department: '',
    university: ''
  });

  const [universities, setUniversities] = useState([]);

  // Fetch university list for dropdown
  useEffect(() => {
    axios.get('http://localhost:5000/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Error loading universities:', err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

 const handleSubmit = async (e) => {
  e.preventDefault();

  const { university, courseName, semester, department } = formData;
  if (!university || !courseName || !semester || !department) {
    alert('Please fill all fields.');
    return;
  }

  try {
    const res = await axios.post('http://localhost:5000/api/courses', formData);
    alert('✅ Course added!');
    // reset form
  } catch (err) {
    console.error('Add course error:', err.response?.data || err.message);
    alert('❌ Failed to add courses.');
  }
};
  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Add Course</h2>
      <form onSubmit={handleSubmit} className="space-y-4">

        <div>
          <label className="block mb-1 font-medium">Course Name</label>
          <input
            type="text"
            name="name"
            placeholder="e.g. B.Tech CSE"
            value={formData.name}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Department</label>
          <input
            type="text"
            name="department"
            placeholder="e.g. Computer Science"
            value={formData.department}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">University</label>
          <select
            name="university"
            value={formData.university}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          >
            <option value="">-- Select University --</option>
            {universities.map((uni) => (
              <option key={uni._id} value={uni._id}>
                {uni.name}
              </option>
            ))}
          </select>
        </div>

        <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddCourses;
