import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PublicDashboard = () => {
  const [universities, setUniversities] = useState([]);

  useEffect(() => {
    // Replace with your actual backend URL
    axios.get('http://localhost:5000/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Error fetching universities:', err));
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h1 className="text-3xl font-bold text-center text-blue-700 mb-8">All Universities</h1>

      <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
        {universities.map((uni, index) => (
          <div key={index} className="bg-white p-5 shadow rounded-xl hover:shadow-lg transition">
            <h2 className="text-xl font-semibold text-blue-600 mb-2">{uni.name}</h2>
            <p className="text-gray-700"><strong>Location:</strong> {uni.location || 'N/A'}</p>
            <p className="text-gray-700"><strong>Courses:</strong> {uni.courses?.join(', ') || 'N/A'}</p>
          </div>
        ))}
      </div>

      {universities.length === 0 && (
        <p className="text-center text-gray-500 mt-10">No universities found.</p>
      )}
    </div>
  );
};

export default PublicDashboard;
