// src/admin-pages/ManageNotes.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaEye, FaDownload, FaUniversity, FaBookOpen } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE
});

const ManageNotes = () => {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Failed to fetch notes:', err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;

    try {
      await API.delete(`/api/resources/${id}`);
      alert('✅ Note deleted');
      fetchNotes(); // Refresh
    } catch (err) {
      console.error('❌ Delete error:', err);
      alert('Failed to delete');
    }
  };

  const handleView = (note) => {
    const fileUrl = `${process.env.REACT_APP_API_BASE}/uploads/notes/${note.file}`;
    window.open(fileUrl, '_blank');
  };

  const handleDownload = (note) => {
    const downloadUrl = `${process.env.REACT_APP_API_BASE}/api/resources/download/${note._id}`;
    window.open(downloadUrl, '_blank');
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-blue-700 flex items-center gap-3">
        <FaBookOpen className="text-green-600" /> Manage Uploaded Notes
      </h2>

      {notes.length === 0 ? (
        <p>No notes found.</p>
      ) : (
        <div className="space-y-8">
          {notes.map((note) => (
            <div key={note._id} className="bg-white shadow-md p-4 rounded-lg border">
              <h3 className="text-xl font-semibold text-blue-800 flex items-center gap-2 mb-2">
                <FaUniversity /> {note.university?.name || 'Unknown University'}
              </h3>
              <p className="text-sm text-gray-700 mb-1">
                <strong>Department:</strong> {note.department}
              </p>
              <p className="text-sm text-gray-700 mb-3">
                <strong>Course:</strong> {note.course} | <strong>Semester:</strong> {note.semester}
              </p>

              <div className="bg-gray-100 p-3 rounded flex justify-between items-center">
                <span className="font-medium text-gray-800">{note.title}</span>
                <div className="flex gap-3">
                  <button
                    onClick={() => handleView(note)}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded flex items-center gap-1"
                  >
                    <FaEye /> View
                  </button>

                  <button
                    onClick={() => handleDownload(note)}
                    className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded flex items-center gap-1"
                  >
                    <FaDownload /> Download
                  </button>

                  <button
                    onClick={() => handleDelete(note._id)}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded flex items-center gap-1"
                  >
                    <FaTrash /> Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
console.log(note);


export default ManageNotes;
