import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  FaUniversity,
  FaPlus,
  FaTrash,
  FaGlobe,
  FaChalkboardTeacher,
  FaBook,
  FaLayerGroup,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const UniversitiesPage = () => {
  const [universities, setUniversities] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [message, setMessage] = useState('');

  const [formData, setFormData] = useState({
    name: '',
    shortName: '',
    description: '',
    website: '',
    logo: '',
    location: '',
    departments: [
      {
        name: '',
        courses: [{ name: '', level: '', duration: '', semesters: '' }]
      }
    ]
  });

  useEffect(() => {
    fetchUniversities();
  }, []);

  const fetchUniversities = async () => {
    try {
      const res = await API.get('/api/universities');
      setUniversities(res.data);
    } catch (err) {
      console.error('❌ Failed to fetch universities:', err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleDepartmentChange = (index, e) => {
    const updated = [...formData.departments];
    updated[index][e.target.name] = e.target.value;
    setFormData(prev => ({ ...prev, departments: updated }));
  };

  const handleCourseChange = (deptIndex, courseIndex, e) => {
    const updated = [...formData.departments];
    updated[deptIndex].courses[courseIndex][e.target.name] = e.target.value;
    setFormData(prev => ({ ...prev, departments: updated }));
  };

  const addDepartment = () => {
    setFormData(prev => ({
      ...prev,
      departments: [...prev.departments, { name: '', courses: [{ name: '', level: '', duration: '', semesters: '' }] }]
    }));
  };

  const addCourse = (deptIndex) => {
    const updated = [...formData.departments];
    updated[deptIndex].courses.push({ name: '', level: '', duration: '', semesters: '' });
    setFormData(prev => ({ ...prev, departments: updated }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post('/api/universities', formData);
      if (res.status === 200 || res.status === 201) {
        setMessage('✅ University added successfully!');
        setFormData({
          name: '',
          shortName: '',
          description: '',
          website: '',
          logo: '',
          location: '',
          departments: [
            {
              name: '',
              courses: [{ name: '', level: '', duration: '', semesters: '' }]
            }
          ]
        });
        fetchUniversities();
        setShowForm(false);
      } else {
        setMessage(`❌ Failed: ${res.data.error || 'Server error'}`);
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('❌ Failed to connect to server');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this university?')) return;

    try {
      const res = await API.delete(`/api/universities/${id}`);
      if (res.status === 200) {
        setUniversities(universities.filter(u => u._id !== id));
      } else {
        alert('❌ Failed to delete university');
      }
    } catch (err) {
      console.error(err);
      alert('❌ Server error while deleting university');
    }
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-blue-700 flex items-center gap-3">
          <FaUniversity className="text-yellow-500" />
          Universities Dashboard
        </h2>
        <button
          onClick={() => setShowForm(!showForm)}
          className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          <FaPlus />
          {showForm ? 'Cancel' : 'Add University'}
        </button>
      </div>

      {message && (
        <div className="mb-4 text-green-700 font-medium text-center">{message}</div>
      )}

      {/* Add University Form */}
      {showForm && (
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow-md border space-y-4 mb-10">
          <div className="grid md:grid-cols-2 gap-4">
            <input name="name" placeholder="University Name *" value={formData.name} onChange={handleChange} required className="border px-3 py-2 rounded" />
            <input name="shortName" placeholder="Short Name (e.g. HPU)" value={formData.shortName} onChange={handleChange} className="border px-3 py-2 rounded" />
            <input name="location" placeholder="Location" value={formData.location} onChange={handleChange} className="border px-3 py-2 rounded" />
            <input name="website" placeholder="Website URL" value={formData.website} onChange={handleChange} className="border px-3 py-2 rounded" />
            <input name="logo" placeholder="Logo URL" value={formData.logo} onChange={handleChange} className="border px-3 py-2 rounded" />
          </div>
          <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} className="w-full border px-3 py-2 rounded" />

          <h4 className="text-lg font-medium text-gray-700 mt-6 flex items-center gap-2"><FaBook /> Departments & Courses</h4>
          {formData.departments.map((dept, deptIndex) => (
            <div key={deptIndex} className="p-4 border rounded bg-gray-50 mt-2 space-y-2">
              <input
                type="text"
                name="name"
                placeholder={`Department ${deptIndex + 1} Name`}
                value={dept.name}
                onChange={(e) => handleDepartmentChange(deptIndex, e)}
                className="w-full border px-2 py-1 rounded"
              />
              {dept.courses.map((course, courseIndex) => (
                <div key={courseIndex} className="grid grid-cols-2 md:grid-cols-4 gap-3">
                  <input name="name" placeholder="Course Name" value={course.name} onChange={(e) => handleCourseChange(deptIndex, courseIndex, e)} className="border px-2 py-1 rounded" />
                  <select name="level" value={course.level} onChange={(e) => handleCourseChange(deptIndex, courseIndex, e)} className="border px-2 py-1 rounded">
                    <option value="">Level</option>
                    <option value="UG">UG</option>
                    <option value="PG">PG</option>
                    <option value="PhD">PhD</option>
                  </select>
                  <input name="duration" placeholder="Duration" value={course.duration} onChange={(e) => handleCourseChange(deptIndex, courseIndex, e)} className="border px-2 py-1 rounded" />
                  <input name="semesters" type="number" placeholder="Semesters" value={course.semesters} onChange={(e) => handleCourseChange(deptIndex, courseIndex, e)} className="border px-2 py-1 rounded" />
                </div>
              ))}
              <button type="button" onClick={() => addCourse(deptIndex)} className="text-sm text-blue-600 mt-2">+ Add Course</button>
            </div>
          ))}
          <button type="button" onClick={addDepartment} className="text-sm text-green-600 mt-2">+ Add Department</button>

          <div className="text-center mt-6">
            <button type="submit" className="bg-indigo-600 text-white px-6 py-2 rounded hover:bg-indigo-700">Submit University</button>
          </div>
        </form>
      )}

      {/* Universities List */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {universities.map((uni) => (
          <div key={uni._id} className="bg-white p-5 rounded-xl shadow border hover:shadow-lg transition">
            <h3 className="text-xl font-semibold text-blue-800 mb-1">{uni.name}</h3>
            <p className="text-sm text-gray-600 mb-2">{uni.description}</p>
            <a href={uni.website} target="_blank" rel="noopener noreferrer" className="inline-flex items-center gap-2 text-blue-600 hover:underline text-sm mb-2">
              <FaGlobe /> Visit Website
            </a>
            <div className="flex justify-end">
              <button
                onClick={() => handleDelete(uni._id)}
                className="bg-red-500 text-white px-4 py-1.5 rounded hover:bg-red-600 text-sm flex items-center gap-1"
              >
                <FaTrash /> Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UniversitiesPage;
