import React from 'react';
import { useParams } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBook, faFileAlt, faListAlt } from '@fortawesome/free-solid-svg-icons';

const departmentData = {
  'sardar-patel-university-mandi': {
    UG: {
      notes: ['SPU UG DSA Notes', 'SPU UG DBMS Notes'],
      papers: {
        2022: ['SPU UG 2022 Midterm'],
        2023: ['SPU UG 2023 Endterm']
      },
      syllabus: ['SPU UG Semester 1', 'SPU UG Semester 2']
    },
    PG: {
      notes: ['SPU PG Cloud Computing', 'SPU PG AI Notes'],
      papers: {
        2022: ['SPU PG 2022 Final'],
        2023: ['SPU PG 2023 Midterm']
      },
      syllabus: ['SPU PG Semester 1', 'SPU PG Semester 2']
    }
  },
  'himachal-pradesh-university': {
    UG: {
      notes: ['HPU UG Java Notes', 'HPU UG OS Notes'],
      papers: {
        2021: ['HPU UG 2021 Final'],
        2022: ['HPU UG 2022 Midterm']
      },
      syllabus: ['HPU UG Semester 3', 'HPU UG Semester 4']
    },
    PhD: {
      notes: ['HPU PhD Thesis Guide'],
      papers: {
        2022: ['HPU PhD Qualifier 2022']
      },
      syllabus: ['HPU PhD Research Methods']
    }
  }
};

const DepartmentPages = () => {
  const { name } = useParams();
  const formattedName = name.toLowerCase();
  const deptName = name.replace(/-/g, ' ').toUpperCase();
  const data = departmentData[formattedName] || {};

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold text-blue-800 mb-6">Departments for {deptName}</h2>
      {Object.entries(data).map(([program, content], index) => (
        <div key={index} className="mb-10">
          <h3 className="text-xl font-bold mb-4 text-green-700 dark:text-green-400">{program} Program</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
              <h4 className="font-semibold text-lg mb-2">
                <FontAwesomeIcon icon={faBook} className="mr-2" />Notes
              </h4>
              <ul>
                {content.notes.map((item, idx) => (
                  <li key={idx} className="mb-1">• {item}</li>
                ))}
              </ul>
            </div>

            <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
              <h4 className="font-semibold text-lg mb-2">
                <FontAwesomeIcon icon={faFileAlt} className="mr-2" />Question Papers
              </h4>
              {Object.entries(content.papers).map(([year, papers], idx) => (
                <div key={idx} className="mb-2">
                  <h5 className="font-medium text-blue-700 dark:text-blue-400">{year}</h5>
                  <ul className="ml-4 list-disc">
                    {papers.map((paper, i) => (
                      <li key={i}>{paper}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>

            <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
              <h4 className="font-semibold text-lg mb-2">
                <FontAwesomeIcon icon={faListAlt} className="mr-2" />Syllabus
              </h4>
              <ul>
                {content.syllabus.map((item, idx) => (
                  <li key={idx} className="mb-1">• {item}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default DepartmentPages;
