import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { FaChalkboardTeacher } from 'react-icons/fa';

const CoursePages = () => {
  const { universityId } = useParams();
  const [courses, setCourses] = useState([]);
  const [grouped, setGrouped] = useState({ UG: [], PG: [], PhD: [], Unknown: [] });

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/courses/university/${universityId}`)
      .then((res) => {
        setCourses(res.data);
        groupCourses(res.data);
      })
      .catch((err) => console.error('Failed to fetch courses:', err));
  }, [universityId]);

  const groupCourses = (courses) => {
    const grouped = { UG: [], PG: [], PhD: [], Unknown: [] };

    courses.forEach((course) => {
      const type = course.type?.toLowerCase();
      if (type === 'ug') grouped.UG.push(course);
      else if (type === 'pg') grouped.PG.push(course);
      else if (type === 'phd') grouped.PhD.push(course);
      else grouped.Unknown.push(course);
    });

    setGrouped(grouped);
  };

  const renderCourses = (title, courseList) => (
    <div className="mb-12">
      <h2 className="text-2xl font-bold text-blue-700 mb-4 border-b pb-1">{title} Courses</h2>
      {courseList.length === 0 ? (
        <p className="text-gray-500 dark:text-gray-400">No {title.toLowerCase()} courses found.</p>
      ) : (
        <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-6">
          {courseList.map((course, i) => (
            <div
              key={i}
              className="bg-white dark:bg-gray-800 shadow-md rounded-xl p-6 border border-green-100 dark:border-gray-700"
            >
              <h3 className="text-xl font-semibold text-green-700 dark:text-green-200 mb-2">{course.courseName}</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                <strong>Semester:</strong> {course.semester}
              </p>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                <strong>Department:</strong> {course.department}
              </p>
       <Link to={`/coursedetailspage/${course._id}/details`}>
  <button className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
    📂 Explore Course Data
  </button>
</Link>


             {/*} <div className="mt-4 flex flex-wrap gap-2">
                <button className="bg-blue-500 hover:bg-blue-600 text-white text-sm px-3 py-1 rounded">📘 Notes</button>
                <button className="bg-purple-500 hover:bg-purple-600 text-white text-sm px-3 py-1 rounded">📄 Syllabus</button>
                <button className="bg-orange-500 hover:bg-orange-600 text-white text-sm px-3 py-1 rounded">📝 Papers</button>
              </div>*/}
            </div>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div className="min-h-screen px-6 py-16 bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-white">
      <h1 className="text-4xl sm:text-5xl font-extrabold text-center mb-12 text-green-800 dark:text-green-300 flex justify-center items-center gap-3">
        <FaChalkboardTeacher className="text-yellow-400 text-5xl" />
        <span>Courses Offered</span>
      </h1>

      {renderCourses('UG', grouped.UG)}
      {renderCourses('PG', grouped.PG)}
      {renderCourses('PhD', grouped.PhD)}
      {renderCourses('Unknown', grouped.Unknown)}
    </div>
  );
};

export default CoursePages;
