import React, { useState } from 'react';
import axios from 'axios';

const AddNotes = () => {
  const [formData, setFormData] = useState({
    university: '',
    course: '',
    semester: '',
    subject: '',
    pdf: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.pdf) {
      alert("Please select a PDF file.");
      return;
    }

    const data = new FormData();
    data.append('university', formData.university);
    data.append('course', formData.course);
    data.append('semester', formData.semester);
    data.append('subject', formData.subject);
    data.append('pdf', formData.pdf);

    try {
      const res = await axios.post('http://localhost:5000/api/notes', data);
      alert('✅ Notes submitted successfully!');
      setFormData({
        university: '',
        course: '',
        semester: '',
        subject: '',
        pdf: null
      });
    } catch (err) {
      console.error(err);
      alert('❌ Failed to submit notes.');
    }
  };

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Add Notes</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1 font-medium">University</label>
          <input
            type="text"
            name="university"
            value={formData.university}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Course</label>
          <input
            type="text"
            name="course"
            value={formData.course}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Semester</label>
          <input
            type="number"
            name="semester"
            value={formData.semester}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Subject</label>
          <input
            type="text"
            name="subject"
            value={formData.subject}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-medium">Upload PDF</label>
          <div className="flex items-center space-x-4">
            <label className="bg-blue-600 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-700">
              Choose File
              <input
                type="file"
                name="pdf"
                accept="application/pdf"
                onChange={handleChange}
                className="hidden"
                required
              />
            </label>
            {formData.pdf && (
              <span className="text-sm text-gray-700">{formData.pdf.name}</span>
            )}
          </div>
        </div>

        <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddNotes;
