// src/pages/CourseDetailsPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CourseDetailsPage = () => {
  const { universityId, department, courseName } = useParams();
  const [semesterCount, setSemesterCount] = useState(0);
  const [resources, setResources] = useState({});
  const [loading, setLoading] = useState({}); // track loading by semester + type

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/universities/${universityId}`)
      .then((res) => {
        const uni = res.data;
        const dept = uni.departments.find((d) => d.name === department);
        const course = dept?.courses.find((c) => c.name === courseName);
        if (course) setSemesterCount(course.semesters);
      })
      .catch((err) => console.error('Error fetching course:', err));
  }, [universityId, department, courseName]);

  const fetchResourceByType = async (semester, type) => {
    const key = `${semester}-${type}`;
    setLoading((prev) => ({ ...prev, [key]: true }));

    try {
      const res = await axios.get(
        `http://localhost:5000/api/resources/${universityId}/${department}/${courseName}/${semester}`
      );

      const filtered = res.data.filter((item) => item.type === type);

      setResources((prev) => ({
        ...prev,
        [semester]: {
          ...prev[semester],
          [type]: filtered,
        },
      }));
    } catch (err) {
      console.error(`Error fetching ${type} for semester ${semester}:`, err);
    }

    setLoading((prev) => ({ ...prev, [key]: false }));
  };

  return (
    <div className="p-6 min-h-screen bg-gray-50 text-gray-800 dark:bg-gray-900 dark:text-white">
      <h1 className="text-3xl font-bold text-blue-700 mb-6">
        {courseName} — {department}
      </h1>

      {semesterCount > 0 ? (
        <div className="space-y-6">
          {[...Array(semesterCount)].map((_, i) => {
            const sem = i + 1;
            const semData = resources[sem] || {};

            return (
              <div
                key={sem}
                className="bg-white dark:bg-gray-800 rounded-xl shadow p-5 border"
              >
                <h2 className="text-xl font-bold text-blue-600 mb-4">
                  Semester {sem}
                </h2>

                <div className="flex gap-4 flex-wrap mb-6">
                  {['syllabus', 'notes', 'papers'].map((type) => (
                    <button
                      key={type}
                      className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                      onClick={() => fetchResourceByType(sem, type)}
                    >
                      {loading[`${sem}-${type}`]
                        ? `Loading ${type}...`
                        : `Show ${type.charAt(0).toUpperCase() + type.slice(1)}`}
                    </button>
                  ))}
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  {/* Syllabus */}
                  <div>
                    <h3 className="font-semibold text-lg text-blue-500">📘 Syllabus</h3>
                    <ul className="text-sm list-disc ml-4 mt-2">
                      {semData.syllabus?.length > 0 ? (
                        semData.syllabus.map((s, idx) => (
                          <li key={idx}>
                            <a
                              href={`http://localhost:5000/uploads/resources/${s.file}`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-600 underline"
                            >
                              {s.title}
                            </a>
                          </li>
                        ))
                      ) : (
                        <li>No syllabus found</li>
                      )}
                    </ul>
                  </div>

                  {/* Notes */}
                  <div>
                    <h3 className="font-semibold text-lg text-green-500">📝 Notes</h3>
                    <ul className="text-sm list-disc ml-4 mt-2">
                      {semData.notes?.length > 0 ? (
                        semData.notes.map((n, idx) => (
                          <li key={idx}>
                            <a
                              href={`http://localhost:5000/uploads/resources/${n.file}`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-600 underline"
                            >
                              {n.title}
                            </a>
                          </li>
                        ))
                      ) : (
                        <li>No notes found</li>
                      )}
                    </ul>
                  </div>

                  {/* Question Papers */}
                  <div>
                    <h3 className="font-semibold text-lg text-red-500">📄 Question Papers</h3>
                    <ul className="text-sm list-disc ml-4 mt-2">
                      {semData.papers?.length > 0 ? (
                        semData.papers.map((p, idx) => (
                          <li key={idx}>
                            <a
                              href={`http://localhost:5000/uploads/resources/${p.file}`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-blue-600 underline"
                            >
                              {p.title} ({p.year})
                            </a>
                          </li>
                        ))
                      ) : (
                        <li>No papers found</li>
                      )}
                    </ul>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <p className="text-gray-500">No semester data available.</p>
      )}
    </div>
  );
};

export default CourseDetailsPage;
