// src/pages/UniversityPage.jsx

import React from 'react';

const universities = [
  {
    name: 'Sardar Patel University, Mandi',
    description: 'A state university providing quality education in various technical and non-technical domains.',
    website: 'https://spumandi.ac.in'
  },
  {
    name: 'Himachal Pradesh University',
    description: 'Renowned university offering UG and PG courses with strong research programs.',
    website: 'https://hpuniv.ac.in'
  },
  {
    name: 'IIT Mandi',
    description: 'An Institute of National Importance offering cutting-edge research and engineering programs.',
    website: 'https://iitmandi.ac.in'
  }
];

export default function UniversityPage() {
  return (
    <div className="p-6 dark:bg-gray-800 dark:text-white bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-bold mb-4">Universities</h2>
      <div className="grid gap-6">
        {universities.map((uni, index) => (
          <div key={index} className="bg-white dark:bg-gray-900 p-4 rounded shadow">
            <h3 className="text-xl font-semibold">{uni.name}</h3>
            <p className="mt-2">{uni.description}</p>
            <a href={uni.website} target="_blank" rel="noopener noreferrer" className="text-blue-500 mt-2 inline-block underline">
              Visit Website
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}
