// src/admin-pages/AdminTrusted.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaTrash,
  FaUserShield,
  FaPlusCircle,
  FaUserCheck,
  FaUserTag,
  FaEnvelope
} from 'react-icons/fa';

const API = axios.create({
  baseURL: 'http://localhost:5000/api', // ensure this is correct for local
});

const AdminTrusted = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    position: '',
    description: '',
    email: '',
    image: null,
  });

  const fetchTrustedPersons = async () => {
    try {
      const res = await API.get('/trusted');
      setTrustedPersons(res.data);
    } catch (error) {
      console.error('Error fetching trusted persons:', error);
    }
  };

  useEffect(() => {
    fetchTrustedPersons();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = new FormData();
      Object.entries(formData).forEach(([key, value]) => data.append(key, value));

      await API.post('/trusted', data);
      fetchTrustedPersons();

      setFormData({
        name: '',
        position: '',
        description: '',
        email: '',
        image: null,
      });
    } catch (error) {
      console.error('Upload failed:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/trusted/${id}`);
      fetchTrustedPersons();
    } catch (error) {
      console.error('Delete failed:', error);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4 flex items-center gap-2">
        <FaUserShield /> Manage Trusted Persons
      </h2>

      <form onSubmit={handleSubmit} className="grid gap-3 mb-6">
        <input
          type="text"
          placeholder="Name"
          value={formData.name}
          required
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="text"
          placeholder="Position"
          value={formData.position}
          required
          onChange={(e) => setFormData({ ...formData, position: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="email"
          placeholder="Email"
          value={formData.email}
          required
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          className="p-2 border rounded"
        />
        <textarea
          placeholder="Description"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          className="p-2 border rounded"
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setFormData({ ...formData, image: e.target.files[0] })}
          required
          className="p-2 border rounded"
        />
        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded flex items-center gap-2"
        >
          <FaPlusCircle /> Add Trusted Person
        </button>
      </form>

      <div className="grid gap-4">
        {trustedPersons.map((person) => (
          <div key={person._id} className="border rounded p-4 flex justify-between items-center">
            <div className="flex gap-4 items-center">
              <img
                src={`http://localhost:5000${person.image}`}
                alt={person.name}
                className="w-16 h-16 object-cover rounded-full"
              />
              <div>
                <h3 className="font-bold text-lg flex items-center gap-1">
                  <FaUserCheck /> {person.name}
                </h3>
                <p className="text-sm text-gray-600 flex items-center gap-1">
                  <FaUserTag /> {person.position}
                </p>
                <p className="text-sm text-gray-600 flex items-center gap-1">
                  <FaEnvelope /> {person.email}
                </p>
                {person.description && (
                  <p className="text-xs mt-1 text-gray-500">{person.description}</p>
                )}
              </div>
            </div>
            <button
              onClick={() => handleDelete(person._id)}
              className="text-red-600 hover:text-red-800"
            >
              <FaTrash />
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminTrusted;
