import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaUpload } from 'react-icons/fa';
import { MdSchool, MdOutlineBook, MdOutlineFileUpload } from 'react-icons/md';
import { RiFilePaper2Line } from 'react-icons/ri';

const UploadResources = () => {
  const [universities, setUniversities] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [semesterOptions, setSemesterOptions] = useState([]);

  const [formData, setFormData] = useState({
    university: '',
    universityName: '',
    department: '',
    subject: '',
    semester: '',
    type: 'notes',
    file: null,
  });

  useEffect(() => {
    axios.get('http://localhost:5000/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Failed to fetch universities:', err));
  }, []);

  useEffect(() => {
    const selectedUni = universities.find(u => u._id === formData.university);
    if (selectedUni) {
      setDepartments(selectedUni.departments || []);
      setFormData(prev => ({
        ...prev,
        universityName: selectedUni.name,
        department: '',
        subject: '',
        semester: ''
      }));
      setSubjects([]);
      setSemesterOptions([]);
    }
  }, [formData.university]);

  useEffect(() => {
    const selectedDept = departments.find(d => d.name === formData.department);
    if (selectedDept) {
      setSubjects(selectedDept.courses || []);
      setFormData(prev => ({ ...prev, subject: '', semester: '' }));
      setSemesterOptions([]);
    }
  }, [formData.department]);

  useEffect(() => {
    const selectedCourse = subjects.find(c => c.name === formData.subject);
    if (selectedCourse) {
      const total = parseInt(selectedCourse.semesters);
      const semesters = Array.from({ length: total }, (_, i) => (i + 1).toString());
      setSemesterOptions(semesters);
    }
  }, [formData.subject]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData(prev => ({ ...prev, file: e.target.files[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      if (key !== 'file') data.append(key, value);
    });
    data.append('file', formData.file);

    try {
      await axios.post(`http://localhost:5000/api/resources/upload`, data);
      alert('✅ Resource uploaded successfully');

      setFormData({
        university: '',
        universityName: '',
        department: '',
        subject: '',
        semester: '',
        type: 'notes',
        file: null,
      });
      setDepartments([]);
      setSubjects([]);
      setSemesterOptions([]);
    } catch (err) {
      console.error('Upload error:', err.response?.data || err.message);
      alert('❌ Failed to upload resource');
    }
  };

  return (
    <div className="p-6 max-w-2xl mx-auto bg-white rounded-xl shadow-md">
      <h2 className="text-3xl font-bold mb-6 text-blue-800 flex items-center gap-2">
        <FaUpload className="text-blue-600" /> Upload Academic Resource
      </h2>

      <form onSubmit={handleSubmit} className="space-y-5">
        <div>
          <label className="block mb-1 font-medium text-gray-700 flex items-center gap-2">
            <MdSchool /> University
          </label>
          <select
            name="university"
            value={formData.university}
            onChange={handleChange}
            required
            className="w-full border p-2 rounded"
          >
            <option value="">Select University</option>
            {universities.map(u => (
              <option key={u._id} value={u._id}>{u.name}</option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1 font-medium text-gray-700">Department</label>
          <select
            name="department"
            value={formData.department}
            onChange={handleChange}
            required
            disabled={!departments.length}
            className="w-full border p-2 rounded"
          >
            <option value="">Select Department</option>
            {departments.map((dept, i) => (
              <option key={i} value={dept.name}>{dept.name}</option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1 font-medium text-gray-700">Subject</label>
          <select
            name="subject"
            value={formData.subject}
            onChange={handleChange}
            required
            disabled={!subjects.length}
            className="w-full border p-2 rounded"
          >
            <option value="">Select Subject</option>
            {subjects.map((subj, i) => (
              <option key={i} value={subj.name}>{subj.name}</option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1 font-medium text-gray-700">Semester</label>
          <select
            name="semester"
            value={formData.semester}
            onChange={handleChange}
            required
            disabled={!semesterOptions.length}
            className="w-full border p-2 rounded"
          >
            <option value="">Select Semester</option>
            {semesterOptions.map(num => (
              <option key={num} value={num}>Semester {num}</option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1 font-medium text-gray-700">Resource Type</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          >
            <option value="notes">Notes</option>
            <option value="papers">Question Paper</option>
            <option value="syllabus">Syllabus</option>
          </select>
        </div>

        <div>
          <label className="block mb-1 font-medium text-gray-700 flex items-center gap-2">
            <MdOutlineFileUpload /> Upload File (PDF)
          </label>
          <input
            type="file"
            accept="application/pdf"
            onChange={handleFileChange}
            required
            className="w-full border p-2 rounded"
          />
        </div>

        <button
          type="submit"
          className="bg-blue-700 text-white px-5 py-2 rounded hover:bg-blue-800 font-semibold flex items-center gap-2"
        >
          <RiFilePaper2Line /> Submit Resource
        </button>
      </form>
    </div>
  );
};

export default UploadResources;
