import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaUniversity,
  FaGlobe,
  FaPlus,
  FaBook,
  FaLayerGroup,
  FaGraduationCap,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const UniversitiesPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    shortName: '',
    website: '',
    location: '',
    departments: [
      {
        name: '',
        courses: [
          {
            name: '',
            level: 'UG',
            duration: '',
            semesters: '',
          },
        ],
      },
    ],
  });

  const [universities, setUniversities] = useState([]);

  useEffect(() => {
    fetchUniversities();
  }, []);

  const fetchUniversities = async () => {
    try {
      const res = await API.get('/api/universities');
      setUniversities(res.data);
    } catch (err) {
      console.error('Failed to fetch universities:', err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleDepartmentChange = (deptIndex, key, value) => {
    const newDepartments = [...formData.departments];
    newDepartments[deptIndex][key] = value;
    setFormData((prev) => ({ ...prev, departments: newDepartments }));
  };

  const handleCourseChange = (deptIndex, courseIndex, field, value) => {
    const newDepartments = [...formData.departments];
    newDepartments[deptIndex].courses[courseIndex][field] = value;
    setFormData((prev) => ({ ...prev, departments: newDepartments }));
  };

  const addCourse = (deptIndex) => {
    const newDepartments = [...formData.departments];
    newDepartments[deptIndex].courses.push({
      name: '',
      level: 'UG',
      duration: '',
      semesters: '',
    });
    setFormData((prev) => ({ ...prev, departments: newDepartments }));
  };

  const addDepartment = () => {
    setFormData((prev) => ({
      ...prev,
      departments: [
        ...prev.departments,
        {
          name: '',
          courses: [
            {
              name: '',
              level: 'UG',
              duration: '',
              semesters: '',
            },
          ],
        },
      ],
    }));
  };

  const handleSubmit = async () => {
    try {
      await API.post('/api/universities', formData);
      alert('✅ University added!');
      setFormData({
        name: '',
        shortName: '',
        website: '',
        location: '',
        departments: [
          {
            name: '',
            courses: [
              {
                name: '',
                level: 'UG',
                duration: '',
                semesters: '',
              },
            ],
          },
        ],
      });
      fetchUniversities(); // Refresh list
    } catch (error) {
      console.error('Error submitting:', error);
      alert('❌ Submission failed!');
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto bg-white rounded-xl shadow-lg">
      {/* Add University Form */}
      <h2 className="text-2xl font-semibold mb-4 text-indigo-700 flex gap-2 items-center">
        <FaUniversity /> Add University
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
        <input
          name="name"
          placeholder="University Name"
          value={formData.name}
          onChange={handleInputChange}
          className="border rounded-lg px-4 py-2 w-full"
        />
        <input
          name="shortName"
          placeholder="Short Name"
          value={formData.shortName}
          onChange={handleInputChange}
          className="border rounded-lg px-4 py-2 w-full"
        />
        <input
          name="website"
          placeholder="Website"
          value={formData.website}
          onChange={handleInputChange}
          className="border rounded-lg px-4 py-2 w-full"
        />
        <input
          name="location"
          placeholder="Location"
          value={formData.location}
          onChange={handleInputChange}
          className="border rounded-lg px-4 py-2 w-full"
        />
      </div>

      {/* Departments */}
      <div className="mt-4">
        <h3 className="text-lg font-semibold flex items-center gap-2 text-gray-800">
          <FaLayerGroup /> Departments & Courses
        </h3>
        {formData.departments.map((dept, deptIdx) => (
          <div key={deptIdx} className="border p-3 rounded-lg mt-4">
            <input
              value={dept.name}
              onChange={(e) =>
                handleDepartmentChange(deptIdx, 'name', e.target.value)
              }
              placeholder={`Department ${deptIdx + 1}`}
              className="mb-2 w-full border rounded-lg px-4 py-2"
            />

            {dept.courses.map((course, courseIdx) => (
              <div key={courseIdx} className="grid grid-cols-1 md:grid-cols-4 gap-2 mb-2">
                <input
                  placeholder="Course Name"
                  value={course.name}
                  onChange={(e) =>
                    handleCourseChange(deptIdx, courseIdx, 'name', e.target.value)
                  }
                  className="border rounded-lg px-3 py-2"
                />
                <select
                  value={course.level}
                  onChange={(e) =>
                    handleCourseChange(deptIdx, courseIdx, 'level', e.target.value)
                  }
                  className="border rounded-lg px-3 py-2"
                >
                  <option value="UG">UG</option>
                  <option value="PG">PG</option>
                  <option value="PhD">PhD</option>
                </select>
                <input
                  placeholder="Duration"
                  value={course.duration}
                  onChange={(e) =>
                    handleCourseChange(deptIdx, courseIdx, 'duration', e.target.value)
                  }
                  className="border rounded-lg px-3 py-2"
                />
                <input
                  placeholder="Semesters"
                  type="number"
                  value={course.semesters}
                  onChange={(e) =>
                    handleCourseChange(deptIdx, courseIdx, 'semesters', e.target.value)
                  }
                  className="border rounded-lg px-3 py-2"
                />
              </div>
            ))}

            <button
              onClick={() => addCourse(deptIdx)}
              className="mt-2 text-green-600 flex items-center gap-2"
            >
              <FaPlus /> Add Course
            </button>
          </div>
        ))}

        <button
          onClick={addDepartment}
          className="mt-4 text-blue-600 flex items-center gap-2"
        >
          <FaPlus /> Add Department
        </button>
      </div>

      <button
        onClick={handleSubmit}
        className="mt-6 w-full bg-indigo-600 text-white py-2 rounded-lg hover:bg-indigo-700 transition"
      >
        Submit University
      </button>

      {/* Universities List */}
      <div className="mt-12">
        <h2 className="text-2xl font-semibold mb-4 text-indigo-700 flex items-center gap-2">
          <FaGraduationCap /> Universities List
        </h2>

        {universities.length === 0 ? (
          <p className="text-gray-500">No universities added yet.</p>
        ) : (
          universities.map((uni, index) => (
            <div key={index} className="border rounded-lg p-4 mb-6 shadow-sm bg-gray-50">
              <h3 className="text-xl font-bold text-gray-800">
                {uni.name} ({uni.shortName})
              </h3>
              {uni.website && (
                <p className="text-sm text-blue-600">
                  🌐 <a href={uni.website} target="_blank" rel="noreferrer">{uni.website}</a>
                </p>
              )}
              <p className="text-sm text-gray-600">📍 {uni.location}</p>

              <div className="mt-4">
                <h4 className="font-semibold text-gray-700 flex items-center gap-2"><FaLayerGroup /> Departments</h4>
                {uni.departments.map((dept, dIdx) => (
                  <div key={dIdx} className="ml-4 mt-2">
                    <p className="font-medium">{dept.name}</p>
                    {dept.courses.map((course, cIdx) => (
                      <div key={cIdx} className="ml-4 text-sm">
                        🎓 {course.name} – {course.level}, {course.duration}, {course.semesters} semesters
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default UniversitiesPage;
