import React from 'react';

const teams = {
  planning: [
    { name: 'Gautav Sharma', role: 'Project Coordinator' },
    { name: 'Anjali Thakur', role: 'UI/UX Designer' },
    { name: 'Karan Verma', role: 'Frontend Developer' },
  ],
  content: [
    { name: 'Ritika Mehta', role: 'Syllabus Curator' },
    { name: 'Soham Rajput', role: 'Notes Manager' },
    { name: 'Aditya Rana', role: 'Question Paper Analyst' },
  ],
  thanks: [
    { name: 'Dr. Suresh Kumar', role: 'Project Mentor' },
    { name: 'Sardar Patel University, Mandi', role: 'Academic Data Provider' },
    { name: 'OpenAI', role: 'AI Support (GPT)' },
  ],
};

const TeamCard = ({ name, role }) => (
  <div className="bg-white p-4 rounded-xl shadow hover:shadow-lg transition">
    <h4 className="text-lg font-semibold text-blue-600">{name}</h4>
    <p className="text-gray-600 text-sm">{role}</p>
  </div>
);

const TeamSection = ({ title, members }) => (
  <section className="mb-12">
    <h2 className="text-2xl font-bold text-blue-700 mb-6">{title}</h2>
    <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-6">
      {members.map((member, index) => (
        <TeamCard key={index} name={member.name} role={member.role} />
      ))}
    </div>
  </section>
);

const Teams = () => {
  return (
    <div className="min-h-screen bg-gray-50 px-6 py-12">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-bold text-center text-blue-800 mb-12">Meet Our Teams</h1>

        <TeamSection title="ðŸš€ Project Planning Team" members={teams.planning} />
        <TeamSection title="ðŸ“š Content Provider Team" members={teams.content} />
        <TeamSection title="ðŸŒŸ Special Thanks To" members={teams.thanks} />
      </div>
    </div>
  );
};

export default Teams;
