import React, { useEffect, useState } from 'react';
import { FaTrash, FaFilePdf, FaUniversity, FaBookOpen } from 'react-icons/fa';
import axios from 'axios';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Notes = () => {
  const [groupedNotes, setGroupedNotes] = useState({});

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');

      const grouped = res.data.reduce((acc, note) => {
        const uniName = note.university?.name || 'Unknown University';
        const deptName = note.department;
        const courseName = note.course;

console.log('note.university:', note.university);

{Object.entries(courses).map(([course, notes]) => {
  console.log(notes); // ✅ safe here
  return (
    <div key={course}>
      ...
    </div>
  );
})}


        if (!acc[uniName]) acc[uniName] = {};
        if (!acc[uniName][deptName]) acc[uniName][deptName] = {};
        if (!acc[uniName][deptName][courseName]) acc[uniName][deptName][courseName] = [];

        acc[uniName][deptName][courseName].push(note);
        return acc;
      }, {});

      setGroupedNotes(grouped);
    } catch (err) {
      console.error('❌ Failed to fetch notes:', err);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;

    try {
      await API.delete(`/api/resources/${id}`);
      fetchNotes();
    } catch (err) {
      console.error('❌ Failed to delete note:', err);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-4xl font-bold mb-10 text-center text-green-700 flex items-center justify-center gap-3">
        <FaBookOpen className="text-green-600" />
        Manage Academic Notes
      </h2>

      {Object.keys(groupedNotes).length === 0 ? (
        <p className="text-center text-gray-600 text-lg">No notes found.</p>
      ) : (
        Object.entries(groupedNotes).map(([university, departments]) => (
          <div
            key={university}
            className="mb-14 rounded-xl shadow-lg border border-green-300 bg-white overflow-hidden"
          >
            {/* University Header */}
            <div className="bg-green-600 text-white py-5 px-4 text-center text-3xl font-semibold border-b border-green-800">
              <div className="inline-flex items-center justify-center gap-3">
                <FaUniversity className="text-white text-3xl" />
                {university}
              </div>
            </div>

            {/* Departments */}
            {Object.entries(departments).map(([department, courses]) => (
              <div key={department} className="py-8 px-4 border-b border-gray-200">
                <h3 className="text-2xl font-bold text-center text-gray-800 mb-6">
                  🏛 <span className="text-blue-700">{department}</span>
                </h3>

                {/* Courses */}
                {Object.entries(courses).map(([course, notes]) => (
                  <div key={course} className="mb-12">
                    <h4 className="text-xl font-bold text-center text-blue-700 mb-6 flex items-center justify-center gap-2">
                      <FaBookOpen className="text-blue-600" />
                      {course}
                    </h4>

                    <div className="grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 px-2 md:px-8">
                      {notes.map((note) => (
                        <div
                          key={note._id}
                          className="bg-gray-50 border border-gray-200 p-5 rounded-lg shadow-sm hover:shadow-md transition-all"
                        >
                          <h5 className="text-lg font-semibold text-gray-800 mb-2">
                            📘 {note.title || 'Untitled Note'}
                          </h5>

                          <p className="text-sm text-gray-700 mb-2">
                            <span className="font-semibold">Semester:</span>{' '}
                            {note.semester}
                          </p>

                          <a
                            href={`${process.env.REACT_APP_API_BASE}/uploads/resources/${note.file}`}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="inline-flex items-center gap-2 text-blue-600 hover:underline text-sm font-medium"
                          >
                            <FaFilePdf className="text-red-600" />
                            View PDF
                          </a>

                          <button
                            onClick={() => handleDelete(note._id)}
                            className="mt-4 bg-red-500 hover:bg-red-600 text-white text-sm px-3 py-1 rounded flex items-center gap-2"
                          >
                            <FaTrash />
                            Delete
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default Notes;
