import React, { useEffect, useState } from 'react';
import { FaTrash, FaFilePdf, FaUniversity, FaBookOpen } from 'react-icons/fa';
import axios from 'axios';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Notes = () => {
  const [notes, setNotes] = useState({});

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const res = await API.get('/api/resources/notes');

      const grouped = res.data.reduce((acc, item) => {
        const universityName = item.university?.name || 'Unknown University';
        const courseName = item.course;

        if (!acc[universityName]) acc[universityName] = {};
        if (!acc[universityName][courseName]) acc[universityName][courseName] = [];

        acc[universityName][courseName].push(item);
        return acc;
      }, {});

      setNotes(grouped);
    } catch (err) {
      console.error('❌ Failed to fetch notes:', err);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this note?')) return;

    try {
      await API.delete(`/api/resources/${id}`);
      fetchNotes(); // Refresh the list
    } catch (err) {
      console.error('❌ Failed to delete note:', err);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-4xl font-bold mb-8 text-center text-blue-800 flex items-center justify-center gap-3">
        <FaBookOpen className="text-4xl text-green-600" /> All Notes (Admin View)
      </h2>

      {Object.keys(notes).length === 0 ? (
        <p className="text-center text-gray-600 text-lg">No notes found.</p>
      ) : (
        Object.entries(notes).map(([university, courses]) => (
          <div key={university} className="mb-12 rounded-xl shadow-lg border bg-white overflow-hidden">
            {/* University Header */}
            <div className="bg-green-700 text-white px-6 py-4 flex items-center gap-3 text-2xl font-semibold">
              <FaUniversity className="text-2xl" /> {university}
            </div>

            <div className="p-6">
              {Object.entries(courses).map(([course, courseNotes]) => (
                <div key={course} className="mb-10">
                  {/* Course Header */}
                  <h3 className="text-xl font-bold text-blue-700 mb-4 border-b-2 pb-1 border-blue-200 flex items-center gap-2">
                    <FaBookOpen className="text-lg text-blue-600" /> {course}
                  </h3>

                  {/* Note Cards */}
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {courseNotes.map((note) => (
                      <div
                        key={note._id}
                        className="bg-white border border-gray-200 p-4 rounded-lg shadow hover:shadow-md transition-all"
                      >
                        <h4 className="text-lg font-bold text-gray-800 mb-1">
                          {note.title || 'Untitled Note'}
                        </h4>

                        <p className="text-sm text-gray-600 mb-1">
                          <span className="font-medium">Semester:</span> {note.semester}
                        </p>

                        <p className="text-sm text-gray-600 mb-1">
                          <span className="font-medium">Department:</span> {note.department}
                        </p>

                        <p className="text-sm text-gray-600 mb-2">
                          <span className="font-medium">Course:</span> {note.course}
                        </p>

                        <a
                          href={`${process.env.REACT_APP_API_BASE}/uploads/resources/${note.file}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="inline-flex items-center gap-2 text-blue-600 hover:underline text-sm font-medium mt-1"
                        >
                          <FaFilePdf className="text-red-600" /> View PDF
                        </a>

                        <button
                          onClick={() => handleDelete(note._id)}
                          className="mt-3 bg-red-500 text-white text-sm px-3 py-1 rounded hover:bg-red-600 flex items-center gap-2"
                        >
                          <FaTrash /> Delete
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default Notes;
