import React from 'react';
import { FaEnvelope, FaUserTie, FaBookReader } from 'react-icons/fa';

const trustedPeople = [
  {
    _id: 1,
    name: 'Shivam Sharma',
    role: 'Verifier',
    email: 'shivam123@gmail.com',
    image: '/images/trusted/shivam.jpg',
  },
  {
    _id: 2,
    name: 'Ankita Verma',
    role: 'Book Inspector',
    email: 'ankita.verma@gmail.com',
    image: '/images/trusted/ankita.jpg',
  },
];

const purchasers = [
  {
    _id: 1,
    name: 'Ravi Kumar',
    email: 'ravi@example.com',
    bookName: 'C++ Programming',
  },
  {
    _id: 2,
    name: 'Pooja Sharma',
    email: 'pooja@gmail.com',
    bookName: 'DBMS Basics',
  },
];

const TrustedPage = () => {
  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      {/* Header */}
      <h1 className="text-3xl font-bold text-center mb-10">ü§ù Our Trusted People</h1>

      {/* Trusted People Section */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-6 mb-12">
        {trustedPeople.map((person) => (
          <div key={person._id} className="bg-white rounded-xl shadow p-4 hover:shadow-lg">
            <img
              src={person.image}
              alt={person.name}
              className="w-full h-40 object-cover rounded-md mb-4"
            />
            <h2 className="text-lg font-bold flex items-center gap-2 text-blue-700">
              <FaUserTie /> {person.name}
            </h2>
            <p className="text-gray-600">{person.role}</p>
            <p className="text-gray-500 flex items-center gap-2">
              <FaEnvelope /> {person.email}
            </p>
          </div>
        ))}
      </div>

      {/* Purchaser Section */}
      <h2 className="text-2xl font-semibold text-center mb-6">üõí Purchasers</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-6">
        {purchasers.map((buyer) => (
          <div key={buyer._id} className="bg-gray-100 rounded-lg p-4 shadow">
            <h3 className="text-md font-semibold flex items-center gap-2">
              <FaBookReader className="text-green-600" /> {buyer.name}
            </h3>
            <p className="text-sm text-gray-700">
              <strong>Book:</strong> {buyer.bookName}
            </p>
            <p className="text-sm text-gray-600">
              <FaEnvelope className="inline mr-1" /> {buyer.email}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TrustedPage;
