import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaFileAlt,
  FaBookOpen,
  FaUniversity,
  FaClipboardList,
  FaBrain,
  FaChalkboardTeacher,
  FaRegFilePdf,
  FaStar,
  FaLightbulb,
  FaQuoteRight
} from 'react-icons/fa';
const Universities = () => {
  const [universities, setUniversities] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/universities')
      .then(res => setUniversities(res.data))
      .catch(err => console.error('Failed to fetch universities:', err));
  }, []);

  return (
    <div className="min-h-screen px-6 py-16 bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-white">
     <h1 className="text-4xl sm:text-5xl font-extrabold text-center mb-12 text-blue-800 dark:text-blue-300 flex justify-center items-center gap-3">
  <FaUniversity className="text-yellow-400 text-5xl" />
  <span className="tracking-wide">Registered Universities</span>
</h1>

      <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
    {universities.map((uni, i) => (
  <div key={i} className="bg-white dark:bg-gray-800 shadow-md rounded-xl p-6 border border-blue-100 dark:border-gray-700 flex flex-col justify-between">
    <div>
      <h2 className="text-2xl font-semibold text-blue-700 dark:text-blue-200 mb-2">{uni.name}</h2>
      <p className="text-sm text-gray-600 dark:text-gray-400 mb-1"><strong>City:</strong> {uni.city || 'N/A'}</p>
      <p className="text-sm text-gray-600 dark:text-gray-400 mb-4"><strong>Email:</strong> {uni.email || 'contact@university.edu'}</p>
    </div>
    
    <button
      onClick={() => console.log(`Explore clicked for ${uni.name}`)} // Replace with actual routing logic
      className="mt-auto self-start bg-blue-600 text-white px-5 py-2 rounded-xl hover:bg-blue-700 transition-all duration-200"
    >
      Explore Content
    </button>
  </div>
))}

      </div>
    </div>
  );
};

export default Universities;
