import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaUser,
  FaEnvelope,
  FaRupeeSign,
  FaBook,
  FaArrowLeft,
  FaArrowRight,
} from 'react-icons/fa';
import { useReadingMode } from '../context/ReadingModeContext';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Book = () => {
  const [books, setBooks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const { readingMode } = useReadingMode();
  const booksPerPage = 20;

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await API.get('/api/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  const currentBooks = books.slice(indexOfFirstBook, indexOfLastBook);
  const totalPages = Math.ceil(books.length / booksPerPage);

  return (
    <div
      className={`min-h-screen transition-all duration-300 ${
        readingMode
          ? 'bg-yellow-50 text-black'
          : 'bg-gradient-to-br from-blue-50 to-white text-gray-800'
      } py-10 px-4`}
    >
      <div className="max-w-7xl mx-auto">
        <div className="mb-8 text-center">
          <h2 className="text-4xl font-bold flex justify-center items-center gap-3">
            <FaBook className="text-blue-600" />
            Available Books
          </h2>
        </div>

        {books.length === 0 ? (
          <p className="text-gray-600 text-center">No books available.</p>
        ) : (
          <>
            {/* Book Cards */}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
              {currentBooks.map((book) => (
                <div
                  key={book._id}
                  className={`rounded-xl shadow-md hover:shadow-lg transition duration-300 p-4 flex flex-col ${
                    readingMode ? 'bg-white' : 'bg-white'
                  }`}
                >
                  <img
                    src={`${process.env.REACT_APP_API_BASE}${book.image}`}
                    alt={book.name}
                    className="w-full h-44 object-cover rounded-lg mb-3"
                  />
                  <div className="font-semibold text-gray-700 mb-1">
                    <span className="text-sm text-gray-600">Book Name:</span>
                    <span className="ml-1 text-gray-900">{book.name}</span>
                  </div>
                  <div className="flex items-center text-sm text-gray-600 mb-1 gap-2">
                    <FaUser className="text-blue-500" />
                    <span className="font-semibold">Seller:</span>
                    <span>{book.seller}</span>
                  </div>
                  <div className="flex items-center text-sm text-gray-600 mb-1 gap-2">
                    <FaEnvelope className="text-green-500" />
                    <span className="font-semibold">Email:</span>
                    <span>{book.email}</span>
                  </div>
                  <div className="flex items-center text-sm text-gray-700 mt-auto gap-2">
                    <FaRupeeSign className="text-yellow-500" />
                    <span className="font-semibold">Price:</span>
                    <span className="font-bold text-gray-900">{book.price}</span>
                  </div>
                </div>
              ))}
            </div>

            {/* Pagination */}
            <div className="flex justify-center items-center mt-10 gap-6">
              <button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
                className={`flex items-center gap-2 px-4 py-2 rounded-lg transition ${
                  currentPage === 1
                    ? 'bg-gray-300 cursor-not-allowed text-gray-600'
                    : 'bg-blue-500 text-white hover:bg-blue-600'
                }`}
              >
                <FaArrowLeft /> Prev
              </button>
              <span className="text-gray-800 font-medium">
                Page {currentPage} of {totalPages}
              </span>
              <button
                onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
                className={`flex items-center gap-2 px-4 py-2 rounded-lg transition ${
                  currentPage === totalPages
                    ? 'bg-gray-300 cursor-not-allowed text-gray-600'
                    : 'bg-blue-500 text-white hover:bg-blue-600'
                }`}
              >
                Next <FaArrowRight />
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Book;
