import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { Menu, X, Sun, Moon } from 'lucide-react';
import AcademicSourceLogo from '../assets/Academic-Source.png';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  useEffect(() => {
    setMenuOpen(false); // Close menu on route change
  }, [location]);

  useEffect(() => {
    document.documentElement.classList.toggle('dark', darkMode);
  }, [darkMode]);

  const NavLinks = () => (
    <>
      <Link to="/" className="hover:text-blue-500 transition">Home</Link>
      <Link to="/aboutus" className="hover:text-blue-500 transition">About Us</Link>
      <Link to="/universities" className="hover:text-blue-500 transition">Universities</Link>
      <Link to="/teams" className="hover:text-blue-500 transition">Our Teams</Link>
      <Link to="/books" className="hover:text-blue-500 transition">Books</Link>
      <Link to="/eplatform" className="hover:text-blue-500 transition">E-Resources</Link>
      <Link to="/trusted" className="hover:text-blue-500 transition">Trusted People</Link>

      {/* 🔒 Role-based links */}
      {token && role === 'admin' && (
        <Link to="/admin" className="hover:text-blue-500 transition">Admin Panel</Link>
      )}
      {token && role === 'user' && (
        <Link to="/user" className="hover:text-blue-500 transition">User Dashboard</Link>
      )}
      {token && role === 'authority' && (
        <Link to="/authority" className="hover:text-blue-500 transition">Authority Panel</Link>
      )}

      {/* 🔐 Login / Logout */}
      {!token ? (
        <Link to="/login" className="hover:text-blue-500 transition">Login</Link>
      ) : (
        <button
          onClick={handleLogout}
          className="hover:text-red-500 transition"
        >
          Logout
        </button>
      )}

      {/* Theme toggle */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="ml-2 p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600"
        title="Toggle theme"
      >
        {darkMode ? <Sun size={18} /> : <Moon size={18} />}
      </button>
    </>
  );

  return (
    <header className="bg-blue-100 dark:bg-gray-900 text-gray-800 dark:text-white shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Logo */}
          <Link to="/" className="flex items-center gap-3 sm:gap-4">
            <img
              src={AcademicSourceLogo}
              alt="Academic Source Logo"
              className="h-12 sm:h-16 md:h-20 w-auto object-contain transition-all duration-300"
            />
            <div className="leading-tight text-left">
              <span className="text-lg sm:text-xl md:text-2xl font-extrabold text-blue-600 dark:text-blue-400 font-serif tracking-wide">
                Academic Source
              </span>
              <p className="text-xs sm:text-sm text-gray-500 dark:text-gray-300">
                "All Your University Content, One Place"
              </p>
            </div>
          </Link>

          {/* Desktop nav */}
          <nav className="hidden md:flex space-x-6 items-center">
            <NavLinks />
          </nav>

          {/* Mobile menu toggle */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setMenuOpen(!menuOpen)}
              className="p-2 rounded-md hover:bg-gray-100 dark:hover:bg-gray-700"
              title="Toggle Menu"
            >
              {menuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile menu */}
      {menuOpen && (
        <div className="md:hidden bg-white dark:bg-gray-900 px-4 pb-4 space-y-3">
          <NavLinks />
        </div>
      )}
    </header>
  );
};

export default Navbar;
