// Responsive Navbar with:
// - Sticky top nav bar
// - Dark mode toggle
// - Sidebar (slide from left)
// - Menu on sidebar, Title on main

import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faBars,
  faTimes,
  faMoon,
  faSun,
  faUniversity
} from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

export default function Navbar() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);
  const toggleDarkMode = () => setDarkMode(!darkMode);

  // Apply dark mode class to html
  useEffect(() => {
    const root = window.document.documentElement;
    if (darkMode) {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className={`fixed top-0 left-0 h-full w-64 bg-blue-600 text-white p-4 z-50 transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 sm:translate-x-0`}>
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-lg font-bold flex items-center">
            <FontAwesomeIcon icon={faUniversity} className="mr-2" /> ARMS
          </h2>
          <button onClick={toggleSidebar} className="sm:hidden">
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>
        <ul className="space-y-3">
          <li><Link to="/" className="hover:underline">Home</Link></li>
          <li><Link to="/departments" className="hover:underline">Departments</Link></li>
          <li><Link to="/university" className="hover:underline">University</Link></li>
          <li><Link to="/contact" className="hover:underline">Contact Us</Link></li>
          <li><Link to="/members" className="hover:underline">Special Members</Link></li>
          <li><Link to="/admin" className="hover:underline">Admin Login</Link></li>
        </ul>
      </div>

      {/* Main content with top navbar */}
      <div className="flex-1 flex flex-col">
        {/* Sticky Top Nav */}
        <header className="sticky top-0 bg-white dark:bg-gray-900 text-gray-800 dark:text-white p-4 shadow flex justify-between items-center z-40">
          <button onClick={toggleSidebar} className="sm:hidden text-xl">
            <FontAwesomeIcon icon={faBars} />
          </button>

          <h1 className="text-xl font-bold hidden sm:block">Academic Resource Management System</h1>

          <button onClick={toggleDarkMode} className="text-xl">
            <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
          </button>
        </header>

        {/* Page content placeholder */}
        <main className="p-6 dark:bg-gray-800 dark:text-white bg-gray-50 flex-1">
          <p>Welcome to the portal! Select a menu from the sidebar.</p>
        </main>
      </div>
    </div>
  );
}
