import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CourseDetailsPage = () => {
  const { courseId } = useParams();
  const [notes, setNotes] = useState([]);
  const [syllabus, setSyllabus] = useState([]);
  const [papers, setPapers] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/notes/course/${courseId}`).then(res => setNotes(res.data));
    axios.get(`http://localhost:5000/api/syllabus/course/${courseId}`).then(res => setSyllabus(res.data));
    axios.get(`http://localhost:5000/api/papers/course/${courseId}`).then(res => setPapers(res.data));
  }, [courseId]);

  const renderSection = (title, items, color) => (
    <div className="mb-10">
      <h2 className={`text-2xl font-semibold text-${color}-700 dark:text-${color}-300 mb-4`}>{title}</h2>
      {items.length === 0 ? (
        <p className="text-gray-500">No {title.toLowerCase()} found.</p>
      ) : (
        <ul className="space-y-2">
          {items.map((item) => (
            <li key={item._id} className="bg-white dark:bg-gray-800 p-4 rounded shadow">
              <p className="font-medium">{item.title}</p>
              <a
                href={`http://localhost:5000/uploads/${item.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 dark:text-blue-300 text-sm underline"
              >
                ðŸ“¥ Download PDF
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );

  return (
    <div className="min-h-screen px-6 py-10 bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-white">
      <h1 className="text-3xl font-bold mb-8 text-center">ðŸ“– Course Data</h1>

      {renderSection('Notes', notes, 'green')}
      {renderSection('Syllabus', syllabus, 'purple')}
      {renderSection('Question Papers', papers, 'orange')}
    </div>
  );
};

export default CourseDetailsPage;
