import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaTrash, FaPlus } from 'react-icons/fa';

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newCourse, setNewCourse] = useState({
    courseName: '',
    department: '',
    university: '',
    semester: ''
  });

  const [universities, setUniversities] = useState([]);

  useEffect(() => {
    fetchCourses();
    fetchUniversities();
  }, []);

  const fetchCourses = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/courses');
      setCourses(res.data);
    } catch (err) {
      console.error('Failed to fetch courses:', err);
    }
  };

  const fetchUniversities = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/universities');
      setUniversities(res.data);
    } catch (err) {
      console.error('Failed to fetch universities:', err);
    }
  };

  const handleChange = (e) => {
    setNewCourse({ ...newCourse, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    const { courseName, department, university, semester } = newCourse;

    if (!courseName || !department || !university || !semester) {
      return alert('Please fill in all fields!');
    }

    try {
      const res = await axios.post('http://localhost:5000/api/courses', newCourse);
      setCourses([...courses, res.data]);
      setNewCourse({ courseName: '', department: '', university: '', semester: '' });
      setShowForm(false);
    } catch (err) {
      console.error('Failed to add course:', err.response?.data || err.message);
      alert('Error adding course.');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/courses/${id}`);
      setCourses(courses.filter(course => course._id !== id));
    } catch (err) {
      console.error('Failed to delete course:', err);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-blue-700">🎓 Manage Courses</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-6 flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        <FaPlus /> {showForm ? 'Cancel' : 'Add Course'}
      </button>

      {showForm && (
        <div className="mb-8 bg-gray-100 p-6 rounded-xl shadow space-y-4">
          <input
            type="text"
            name="courseName"
            placeholder="Course Name (e.g., B.Tech CSE)"
            value={newCourse.courseName}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded focus:outline-none focus:ring focus:ring-blue-300"
          />
          <input
            type="text"
            name="department"
            placeholder="Department (e.g., Computer Science)"
            value={newCourse.department}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded focus:outline-none focus:ring focus:ring-blue-300"
          />
          <input
            type="number"
            name="semester"
            placeholder="Semester (e.g., 1)"
            value={newCourse.semester}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded focus:outline-none focus:ring focus:ring-blue-300"
          />
          <select
            name="university"
            value={newCourse.university}
            onChange={handleChange}
            className="w-full border px-3 py-2 rounded focus:outline-none focus:ring focus:ring-blue-300"
          >
            <option value="">Select University</option>
            {universities.map((uni) => (
              <option key={uni._id} value={uni._id}>{uni.name}</option>
            ))}
          </select>

          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-5 py-2 rounded hover:bg-green-700"
          >
            Submit Course
          </button>
        </div>
      )}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {courses.map((course) => (
          <div
            key={course._id}
            className="bg-white rounded-xl shadow p-4 border border-gray-200 dark:bg-gray-800 dark:border-gray-700"
          >
            <h3 className="text-xl font-semibold text-blue-800 mb-1">{course.courseName}</h3>
            <p className="text-sm text-gray-600 dark:text-gray-400 mb-1">{course.department}</p>
            <p className="text-sm text-gray-600 dark:text-gray-400 mb-1">Semester: {course.semester}</p>
            <p className="text-sm text-gray-500 dark:text-gray-400 mb-3">
              {universities.find(u => u._id === course.university)?.name || 'Unknown University'}
            </p>
            <button
              onClick={() => handleDelete(course._id)}
              className="inline-flex items-center gap-2 bg-red-500 text-white text-sm px-3 py-1 rounded hover:bg-red-600"
            >
              <FaTrash /> Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Courses;
