import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaTrash,
  FaUser,
  FaEnvelope,
  FaRupeeSign,
  FaBook,
  FaArrowLeft,
  FaArrowRight,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Book = () => {
  const [books, setBooks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const booksPerPage = 6;

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await API.get('/api/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const deleteBook = async (id) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      try {
        await API.delete(`/api/books/${id}`);
        fetchBooks(); // Refresh book list
      } catch (error) {
        console.error('Error deleting book:', error);
      }
    }
  };

  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  const currentBooks = books.slice(indexOfFirstBook, indexOfLastBook);
  const totalPages = Math.ceil(books.length / booksPerPage);

  return (
    <div className="p-6 min-h-screen bg-gray-100">
      <h2 className="text-3xl font-bold text-gray-800 mb-6 flex items-center gap-2">
        <FaBook className="text-blue-600" /> Available Books
      </h2>

      {books.length === 0 ? (
        <p className="text-gray-600">No books available.</p>
      ) : (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {currentBooks.map((book) => (
              <div
                key={book._id}
                className="bg-white rounded-2xl shadow-md hover:shadow-lg transition duration-300 p-5 flex flex-col"
              >
                <img
                  src={`${process.env.REACT_APP_API_BASE}${book.image}`}
                  alt={book.name}
                  className="w-full h-48 object-cover rounded-lg mb-4"
                />
                <h3 className="text-xl font-semibold text-gray-800 mb-1">{book.name}</h3>
                <p className="text-gray-600 text-sm flex items-center gap-2 mb-1">
                  <FaUser className="text-blue-500" /> {book.seller}
                </p>
                <p className="text-gray-600 text-sm flex items-center gap-2 mb-1">
                  <FaEnvelope className="text-green-500" /> {book.email}
                </p>
                <p className="text-gray-700 font-medium flex items-center gap-2 text-lg mt-2">
                  <FaRupeeSign className="text-yellow-500" /> {book.price}
                </p>
                <button
                  onClick={() => deleteBook(book._id)}
                  className="mt-auto bg-red-500 text-white px-3 py-2 rounded-lg hover:bg-red-600 transition flex items-center justify-center gap-2 text-sm"
                >
                  <FaTrash /> Delete
                </button>
              </div>
            ))}
          </div>

          {/* Pagination */}
          <div className="flex justify-center items-center mt-8 gap-4">
            <button
              onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg ${
                currentPage === 1
                  ? 'bg-gray-300 cursor-not-allowed'
                  : 'bg-blue-500 text-white hover:bg-blue-600'
              }`}
            >
              <FaArrowLeft /> Prev
            </button>
            <span className="text-gray-700 font-medium">
              Page {currentPage} of {totalPages}
            </span>
            <button
              onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
              disabled={currentPage === totalPages}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg ${
                currentPage === totalPages
                  ? 'bg-gray-300 cursor-not-allowed'
                  : 'bg-blue-500 text-white hover:bg-blue-600'
              }`}
            >
              Next <FaArrowRight />
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Book;
