import React from 'react';

const teams = {
  planning: [
    {
      name: 'Gautav Sharma',
      role: 'Project Coordinator',
      email: 'gautav@example.com',
      university: 'Sardar Patel University, Mandi',
      photo: 'https://i.pravatar.cc/150?img=1',
    },
    {
      name: 'Anjali Thakur',
      role: 'UI/UX Designer',
      email: 'anjali@example.com',
      university: 'Sardar Patel University, Mandi',
      photo: 'https://i.pravatar.cc/150?img=2',
    },
    {
      name: 'Karan Verma',
      role: 'Frontend Developer',
      email: 'karan@example.com',
      university: 'Sardar Patel University, Mandi',
      photo: 'https://i.pravatar.cc/150?img=3',
    },
  ],
  content: [
    {
      name: 'Ritika Mehta',
      role: 'Syllabus Curator',
      email: 'ritika@example.com',
      university: 'Sardar Patel University, Mandi',
      photo: 'https://i.pravatar.cc/150?img=4',
    },
    {
      name: 'Soham Rajput',
      role: 'Notes Manager',
      email: 'soham@example.com',
      university: 'Sardar Patel University, Mandi',
      photo: 'https://i.pravatar.cc/150?img=5',
    },
    {
      name: 'Aditya Rana',
      role: 'Question Paper Analyst',
      email: 'aditya@example.com',
      university: 'Sardar Patel University, Mandi',
      photo: 'https://i.pravatar.cc/150?img=6',
    },
  ],
  thanks: [
    {
      name: 'Dr. Suresh Kumar',
      role: 'Project Mentor',
      email: 'suresh.kumar@spu.ac.in',
      university: 'Sardar Patel University, Mandi',
      photo: 'https://i.pravatar.cc/150?img=7',
    },
    {
      name: 'SPU, Mandi',
      role: 'Academic Data Provider',
      email: 'info@spu.ac.in',
      university: 'Sardar Patel University, Mandi',
      photo: 'https://i.pravatar.cc/150?img=8',
    },
    {
      name: 'OpenAI',
      role: 'AI Assistant (GPT)',
      email: 'support@openai.com',
      university: 'N/A',
      photo: 'https://i.pravatar.cc/150?img=9',
    },
  ],
};

const TeamCard = ({ member }) => (
  <div className="bg-white rounded-2xl shadow-lg hover:shadow-2xl transition transform hover:-translate-y-1 p-5 text-center">
    <img
      src={member.photo}
      alt={member.name}
      className="w-24 h-24 rounded-full mx-auto mb-4 border-4 border-blue-300 shadow"
    />
    <h3 className="text-lg font-semibold text-blue-700">{member.name}</h3>
    <p className="text-sm text-gray-700">{member.role}</p>
    <p className="text-sm text-gray-500 mt-1">{member.university}</p>
    <a
      href={`mailto:${member.email}`}
      className="text-blue-600 text-sm mt-2 block hover:underline"
    >
      {member.email}
    </a>
  </div>
);

const TeamSection = ({ title, members }) => (
  <section className="mb-16">
    <h2 className="text-3xl font-bold text-center text-blue-800 mb-10">{title}</h2>
    <div className="grid gap-8 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 justify-items-center">
      {members.map((member, idx) => (
        <TeamCard key={idx} member={member} />
      ))}
    </div>
  </section>
);

const Teams = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-100 px-6 py-16">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-5xl font-extrabold text-center text-blue-900 mb-20 tracking-tight">
          Our Awesome Teams
        </h1>

        <TeamSection title="ðŸš€ Project Planning Team" members={teams.planning} />
        <TeamSection title="ðŸ“š Content Provider Team" members={teams.content} />
        <TeamSection title="ðŸŒŸ Special Thanks" members={teams.thanks} />
      </div>
    </div>
  );
};

export default Teams;
