import React from 'react';
import { Link } from 'react-router-dom';

const departmentCourses = [
  {
    course: 'Undergraduate (UG)',
    departments: [
      {
        name: 'Computer Science',
        programs: ['B.Tech CSE', 'B.Sc CS']
      },
      {
        name: 'Electrical Engineering',
        programs: ['B.Tech EE']
      },
      {
        name: 'Mechanical Engineering',
        programs: ['B.Tech ME']
      }
    ]
  },
  {
    course: 'Postgraduate (PG)',
    departments: [
      {
        name: 'Computer Applications',
        programs: ['MCA', 'M.Sc IT']
      },
      {
        name: 'Power Systems',
        programs: ['M.Tech Power Systems']
      },
      {
        name: 'Thermal Engineering',
        programs: ['M.Tech Thermal']
      }
    ]
  },
  {
    course: 'PhD',
    departments: [
      {
        name: 'AI Research',
        programs: ['PhD in AI']
      },
      {
        name: 'VLSI Design',
        programs: ['PhD in VLSI']
      },
      {
        name: 'Mechanical Dynamics',
        programs: ['PhD in Mech']
      }
    ]
  }
];

const Departments = () => {
  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold text-blue-800 mb-4">Departments by Course</h2>
      {departmentCourses.map((group, idx) => (
        <div key={idx} className="mb-8">
          <h3 className="text-xl font-semibold text-green-700 mb-3">{group.course}</h3>
          {group.departments.map((dept, deptIdx) => (
            <div key={deptIdx} className="mb-4">
              <h4 className="text-lg font-semibold text-blue-600 mb-2">{dept.name}</h4>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                {dept.programs.map((program, pIdx) => (
                  <Link
                    key={pIdx}
                    to={`/departments/${program.toLowerCase().replace(/\s+/g, '-')}`}
                    className="bg-blue-100 p-3 rounded shadow hover:bg-blue-200 transition block text-center"
                  >
                    {program}
                  </Link>
                ))}
              </div>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Departments;
