// src/admin-pages/AdminTrusted.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  FaTrash,
  FaUserShield,
  FaPlusCircle,
  FaUserCheck,
  FaUserTag,
} from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE || 'http://localhost:5000/api',
});

const AdminTrusted = () => {
  const [trustedPersons, setTrustedPersons] = useState([]);
  const [purchasers, setPurchasers] = useState([]);
  const [formData, setFormData] = useState({
    type: 'trusted',
    name: '',
    email: '',
    role: '',
    image: null,
  });
  const [preview, setPreview] = useState(null);

  const fetchData = async () => {
    try {
      const [trustedRes, purchaserRes] = await Promise.all([
        API.get('/trusted'),
        API.get('/purchasers'),
      ]);
      setTrustedPersons(trustedRes.data);
      setPurchasers(purchaserRes.data);
    } catch (err) {
      console.error('Error fetching data:', err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      const file = files[0];
      setFormData({ ...formData, image: file });
      if (file) {
        const reader = new FileReader();
        reader.onloadend = () => setPreview(reader.result);
        reader.readAsDataURL(file);
      } else {
        setPreview(null);
      }
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      data.append(key, value);
    });

    try {
      await API.post(`/${formData.type}`, data);
      fetchData();
      alert('Successfully added!');
      setFormData({ type: 'trusted', name: '', email: '', role: '', image: null });
      setPreview(null);
    } catch (err) {
      console.error('Upload failed:', err.response?.data || err.message);
      alert('Upload failed. Check console for details.');
    }
  };

  const handleDelete = async (id, type) => {
    try {
      await API.delete(`/${type}/${id}`);
      fetchData();
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  return (
    <div className="p-6 space-y-10">
      <h1 className="text-2xl font-bold flex items-center gap-2 text-blue-700">
        <FaUserShield className="text-xl" /> Trusted & Purchasers Manager
      </h1>

      {/* Form */}
      <form
        onSubmit={handleSubmit}
        encType="multipart/form-data"
        className="bg-white dark:bg-gray-800 p-4 rounded-xl shadow-md grid gap-4 md:grid-cols-2 lg:grid-cols-3"
      >
        <select
          name="type"
          value={formData.type}
          onChange={handleChange}
          className="border p-2 rounded"
        >
          <option value="trusted">Trusted Person</option>
          <option value="purchasers">Purchaser</option>
        </select>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Full Name"
          required
          className="border p-2 rounded"
        />
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          required
          className="border p-2 rounded"
        />
        <input
          type="text"
          name="role"
          value={formData.role}
          onChange={handleChange}
          placeholder="Role / Note"
          className="border p-2 rounded"
        />
        <input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleChange}
          required
          className="border p-2 rounded"
        />
        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center justify-center"
        >
          <FaPlusCircle className="mr-2" /> Add
        </button>
        {preview && (
          <img
            src={preview}
            alt="Preview"
            className="w-24 h-24 object-cover border rounded-lg"
          />
        )}
      </form>

      {/* Trusted Persons */}
      <section>
        <h2 className="text-lg font-semibold flex items-center gap-2 text-green-700 mb-2">
          <FaUserCheck /> Trusted Persons
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {trustedPersons.map((person) => (
            <div key={person._id} className="bg-white rounded-lg p-4 shadow relative">
              <img
                src={`${process.env.REACT_APP_API_BASE}/${person.image}`}
                alt={person.name}
                className="w-full h-40 object-cover rounded"
              />
              <div className="mt-2">
                <h3 className="font-bold">{person.name}</h3>
                <p>{person.email}</p>
                <p className="text-sm text-gray-600">{person.role}</p>
              </div>
              <button
                className="absolute top-2 right-2 text-red-500 hover:text-red-700"
                onClick={() => handleDelete(person._id, 'trusted')}
              >
                <FaTrash />
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* Purchasers */}
      <section>
        <h2 className="text-lg font-semibold flex items-center gap-2 text-purple-700 mb-2">
          <FaUserTag /> Purchasers
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {purchasers.map((buyer) => (
            <div key={buyer._id} className="bg-white rounded-lg p-4 shadow relative">
              <img
                src={`${process.env.REACT_APP_API_BASE}/${buyer.image}`}
                alt={buyer.name}
                className="w-full h-40 object-cover rounded"
              />
              <div className="mt-2">
                <h3 className="font-bold">{buyer.name}</h3>
                <p>{buyer.email}</p>
                <p className="text-sm text-gray-600">{buyer.role}</p>
              </div>
              <button
                className="absolute top-2 right-2 text-red-500 hover:text-red-700"
                onClick={() => handleDelete(buyer._id, 'purchasers')}
              >
                <FaTrash />
              </button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default AdminTrusted;
