import React, { useState } from 'react';
import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000',
});

const UserRegister = () => {
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState('');
  const [code, setCode] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const sendVerificationCode = async () => {
    try {
      await API.post('/api/user/register/send-code', { email });
      setFormData((prev) => ({ ...prev, email }));
      setMessage('Verification code sent!');
      setStep(2);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to send code');
    }
  };

  const verifyAndRegister = async () => {
    try {
      await API.post('/api/user/register/verify', { ...formData, code });
      setMessage('Registration successful! You can now log in.');
      setStep(3);
    } catch (err) {
      setError(err.response?.data?.error || 'Registration failed');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
        <h2 className="text-xl font-bold mb-4">User Registration</h2>
        {error && <p className="text-red-500 mb-3">{error}</p>}
        {message && <p className="text-green-600 mb-3">{message}</p>}

        {step === 1 && (
          <>
            <label className="block mb-2">Enter your Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full px-3 py-2 border rounded mb-4"
            />
            <button
              onClick={sendVerificationCode}
              className="w-full bg-blue-600 text-white py-2 rounded"
            >
              Send Verification Code
            </button>
          </>
        )}

        {step === 2 && (
          <>
            <label className="block mb-2">Name</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) =>
                setFormData((prev) => ({ ...prev, name: e.target.value }))
              }
              className="w-full px-3 py-2 border rounded mb-4"
            />
            <label className="block mb-2">Password</label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) =>
                setFormData((prev) => ({ ...prev, password: e.target.value }))
              }
              className="w-full px-3 py-2 border rounded mb-4"
            />
            <label className="block mb-2">Enter Code</label>
            <input
              type="text"
              value={code}
              onChange={(e) => setCode(e.target.value)}
              className="w-full px-3 py-2 border rounded mb-4"
            />
            <button
              onClick={verifyAndRegister}
              className="w-full bg-green-600 text-white py-2 rounded"
            >
              Verify and Register
            </button>
          </>
        )}

        {step === 3 && (
          <div className="text-center">
            <p className="text-green-700 font-semibold">ðŸŽ‰ You are registered!</p>
            <p className="text-blue-500 mt-2">You can now login from the login page.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserRegister;
