import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaFilePdf, FaPlus, FaUpload, FaTrash } from 'react-icons/fa';

const Syllabus = () => {
  const [syllabusList, setSyllabusList] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newSyllabus, setNewSyllabus] = useState({
    university: '',
    course: '',
    semester: '',
    subject: '',
    pdf: null
  });

  // Load all syllabus entries
  useEffect(() => {
    axios.get('http://localhost:5000/api/syllabus')
      .then(res => setSyllabusList(res.data))
      .catch(err => console.error('Error fetching syllabus:', err));
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setNewSyllabus({
      ...newSyllabus,
      [name]: files ? files[0] : value
    });
  };

  // Submit new syllabus form
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    for (let key in newSyllabus) {
      formData.append(key, newSyllabus[key]);
    }

    try {
      const res = await axios.post('http://localhost:5000/api/syllabus', formData);
      setSyllabusList([...syllabusList, res.data]);
      setNewSyllabus({ university: '', course: '', semester: '', subject: '', pdf: null });
      setShowForm(false);
    } catch (err) {
      console.error('Upload error:', err);
      alert('Failed to upload syllabus.');
    }
  };

  // Delete syllabus entry
  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this syllabus entry?')) return;

    try {
      const res = await axios.delete(`http://localhost:5000/api/syllabus/${id}`);
      if (res.status === 200) {
        setSyllabusList(syllabusList.filter(item => item._id !== id));
      } else {
        alert('Failed to delete');
      }
    } catch (err) {
      console.error('Delete error:', err);
      alert('Failed to delete entry.');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-blue-700">📘 Manage Syllabus</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-6 flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        <FaPlus />
        {showForm ? 'Cancel' : 'Add Syllabus'}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} className="mb-8 bg-gray-100 p-6 rounded-xl shadow space-y-4">
          {['university', 'course', 'semester', 'subject'].map((field) => (
            <input
              key={field}
              type={field === 'semester' ? 'number' : 'text'}
              name={field}
              placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
              value={newSyllabus[field]}
              onChange={handleChange}
              className="w-full border px-3 py-2 rounded focus:outline-none focus:ring focus:ring-blue-300"
              required
            />
          ))}

          <div>
            <label className="block mb-1 font-medium text-gray-700">Upload PDF</label>
            <label className="bg-blue-600 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-700 inline-flex items-center gap-2">
              <FaUpload />
              Choose File
              <input
                type="file"
                name="pdf"
                accept="application/pdf"
                onChange={handleChange}
                className="hidden"
                required
              />
            </label>
            {newSyllabus.pdf && (
              <span className="ml-3 text-sm text-gray-700">{newSyllabus.pdf.name}</span>
            )}
          </div>

          <button
            type="submit"
            className="bg-green-600 text-white px-5 py-2 rounded hover:bg-green-700"
          >
            Submit Syllabus
          </button>
        </form>
      )}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {syllabusList.map((item) => (
          <div
            key={item._id}
            className="bg-white rounded-xl shadow p-4 border border-gray-200 dark:bg-gray-800 dark:border-gray-700"
          >
            <h3 className="text-xl font-semibold text-blue-800 mb-1">
              {item.subject} (Sem {item.semester})
            </h3>
            <p className="text-sm text-gray-600 dark:text-gray-400 mb-1">
              {item.course} - {item.university}
            </p>
            <a
              href={`http://localhost:5000/uploads/${item.pdf}`}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center gap-2 text-blue-600 text-sm hover:underline"
            >
              <FaFilePdf /> View PDF
            </a>
            <button
              onClick={() => handleDelete(item._id)}
              className="mt-3 inline-flex items-center gap-3 bg-red-500 text-white text-sm px-3 py-1 rounded hover:bg-red-600"
            >
              <FaTrash /> Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Syllabus;
